INSERT INTO `birds` (`bird_id`, `bird_name`, `bird_commonname`, `bird_sciname`, `bird_description`, `bird_habitat`, `bird_family_id`) VALUES
(1, 'นกเป็ดแดง', 'Lesser Whistling-duck', 'Dendrocygna javanica', 'ลำตัวทั่วไปสีน้ำตาลแกมเหลือง กระหม่อม ท้ายทอย และหลังสีน้ำตาลเข้ม ขนคลุมไหล่และหางสีน้ำตาลแดง ขนปลายปีกเทาดำ ปากและขาดำ ขณะบินต่างจากนกเป็ดน้ำชนิดอื่นที่หัวและคออยู่ในระดับต่ำกว่าลำตัวเล็กน้อย ปีกกว้างและกลมกว่านกเป็ดน้ำอื่น ๆ บินตรง กระพือปีกไม่เร็วมาก', 'เอเชียตะวันออกเฉียงใต้ จีน ไต้หวัน อินเดีย และในประเทศไทยพบได้ทั่วไปทุกภาค', '1'),
(2, 'นกเป็ดคับแค', 'Cotton Pygmy-goose', 'Nettapus coromandelianus', 'ปากและแข้งสีดำ ตัวผู้ : หัวและลำตัวสีขาว กระหม่อมสีดำ มีเส้นรอบคอสีดำเหลือบเขียว หลังสีดำเหลือบเขียว ข้างลำตัวสีเทา ท้องสีขาว ขณะบินเห็นแถบขาวขนาดใหญ่พาดตลอดปีก, ตัวเมีย : แถบคาดตาสีน้ำตาลเข้ม หัว คอ และลำตัวด้านล่างสีเนื้อ กระหม่อมสีน้ำตาลเข้ม อกมีลายประสีน้ำตาลกระจาย ขณะบินปีกดำมีขอบจากปลายขนกลางปีกสีขาว', 'สามารถพบได้ทุกภาค', '1'),
(3, 'นกเป็ดลาย', 'Garganey', 'Anas querquedula', 'ปากสีเทาดำ ตัวผู้ : หัวสีน้ำตาลเข้ม คิ้วยาวหนา ไหล่และหลังมีขนยาวสีขาวสลับดำ ข้างลำตัวสีเทา ท้องขาว ขณะบินแถบปีกสีเขียวและขาว, ตัวเมีย : ลำตัวเป็นลายสีน้ำตาล ใบหน้ามีแถบสีน้ำตาลเข้มสองแถบ แถบปีกสีน้ำตาลเข้ม', 'หนองจรเข้ จังหวัดสุโขทัย', '1'),
(4, 'นกเป็ดปากพลั่ว', 'Northern Shoveler', 'Spatula clypeata', 'ปากใหญ่และยาว ปลายปากแผ่กว้างกว่าเป็ดชนิดอื่น ๆ แข้งและตีนสีส้ม ตัวผู้ : ปากดำ หัวและคอตอนบนสีเขียวเข้มเป็นมัน ตาสีเหลือง อกขาว ท้องและสีข้างน้ำตาลแดง ปีกมีแถบฟ้าแกมเทา ขาวและเขียว, ตัวผู้ผลัดขน : คล้ายตัวเมียแต่ปากดำ, ตัวเมีย : ตาสีน้ำตาลเข้ม ปากสีเทาขอบส้ม ลำตัวเป็นลายสีน้ำตาล', 'บึงบอระเพ็ด จังหวัดนครสวรรค์', '1'),
(5, 'นกเป็ดหางแหลม', 'Northern Pintail', 'Anas acuta', 'คอค่อนข้างยาว ปลายหางแหลมยาว ตัวผู้ : ปากเทา สันและขอบปากดำ หัวสีน้ำตาลเข้มต่อเนื่องกับท้ายทอย มีเส้นสีขาวเรียวลงไปข้างคอ คอ อกและท้องสีขาว ลำตัวสีเทา ด้านท้ายลำตัวสีครีม ก้นและหางดำขอบหางขาว ขณะบินจะเห็นแถบปีกสีเขียวและขอบปีกขาว, ตัวเมีย : ปากเทา ขอบปากสีดำหนากว่าตัวผู้ หัวสีน้ำตาล ลำตัวเป็นลายสีน้ำตาล ท้องสีขาว ปลายหางแหลม แต่สั้นกว่าตัวผู้', 'จังหวัดอุตรดิตถ์', '1'),
(6, 'นกเป็ดเทาพันธุ์อินเดีย', 'Indian Spot-billed Duck', 'Anas poecilorhyncha', 'ปากดำปลายปากเหลือง แข้งและตีนแดง ตัวผู้ : มีจุดกลมเล็ก ๆ สีแดงที่หัวตา หัวและคอสีเทาแกมน้ำตาลอ่อนหรือค่อนข้างขาว กระหม่อมดำ แถบคาดตาดำ ลำตัวเป็นลายเกล็ดเล็ก ๆ สีน้ำตาล ลายค่อย ๆ ใหญ่ขึ้นทางด้านท้าย ขนคลุมโคนหางด้านล่างสีน้ำตาลเข้ม ปีกมีแถบขาวและเขียว, ตัวเมีย : สีน้ำตาลมากกว่าตัวผู้ ไม่มีจุดกลมสีแดงที่หัวตา', 'บึงบอระเพ็ด และ จังหวัดนครสวรรค์', '1'),
(7, 'นกเป็ดปากสั้น', 'Eurasian Wigeon', 'Anas penelope', 'หัวค่อนข้างกลม ปากเทาปลายดำ สั้นเมื่อเทียบกับเป็ดอื่น ๆ ตัวผู้ : หัวสีส้มแกมน้ำตาล หน้าผากถึงกระหม่อมสีเหลือง อกสีชมพู ลำตัวเทา ท้องขาว ก้นสีดำ ขณะบินปีกมีแถบขาวและเขียว, ตัวเมีย : สีน้ำตาลเข้ม อกและสีข้างแกมสีน้ำตาลแดงมากกว่าส่วนอื่น', 'บึงบอระเพ็ด และ จังหวัดนครสวรรค์', '1'),
(8, 'นกเป็ดปีกเขียว', 'Eurasian Teal', 'Anas crecca', 'ตัวผู้ : ปากดำ หัวและคอสีน้ำตาลแดง มีแถบโค้งสีเขียวขนาดใหญ่จากเบ้าตาถึงท้ายทอย ลำตัวสีเทา ก้นสีดำมีแถบใหญ่สีเหลือง, ตัวเมีย : ลำตัวเป็นลายสีน้ำตาล แถบปีกเขียวและขาว', 'หนองจรเข้ จังหวัดสุโขทัย', '1'),
(9, 'นกเป็ดผีเล็ก', 'Little Grebe', 'Tachybaptus ruficollis', 'คล้ายนกเป็ดน้ำหางสั้นกุด ปากแหลมสีเหลืองอ่อน หน้าผาก ท้ายทอยถึงหลังน้ำตาลเข้ม หน้า ข้างคอ อก และท้องน้ำตาลแกมเหลือง  ขนชุดผสมพันธุ์ : หน้า ข้างคอน้ำตาลแดงเข้ม สีข้างน้ำตาลเข้ม ปากดำ โคนปากเหลือง ตาเหลือง', 'อาฟริกา, ยุโรป, อินเดีย, จีน, ไหหลำ, ไตหวัน, ฟิลิปปินส์, ซุนดาห์, นิวกีนี, โซโลมอน, และประเทศต่างๆทางภาคตะวันออกเฉียงใต้ของทวีปเอเชีย', '2'),
(10, 'นกปากห่าง', 'Asian Openbill', 'Anastomus oscitans', 'ปากสีน้ำตาลแกมเหลือง กลางปากค่อนไปทางปลายเปิดเป็นช่องว่าง ขนลำตัวเทา ขนโคนปีก ขนปีกบิน และหางดำ แข้งและตีนชมพูคล้ำ ขนชุดผสมพันธุ์ : ขนลำตัวขาวมากขึ้น แข้งและตีนแดง', 'ภาคกลางและภาคอีสาน', '3'),
(11, 'นกช้อนหอยดำเหลือบ', 'Glossy Ibis', 'Plegadis falcinellus', 'ปากโค้งยาวสีเหลืองคล้ำ หัว คอ และลำตัวด้านบนสีน้ำตาลเข้ม รอบใบหน้าเป็นเส้นสีขาวแกมฟ้า มีลายขีดขาวกระจายที่หัวและคอ ปีกและหลังตอนท้ายเขียวเข้มเหลือบเป็นมัน แข้งและตีนน้ำตาลเข้ม ขนชุดผสมพันธุ์ : หัว คอ และลำตัวสีน้ำตาลแดงเข้มแกมม่วง หน้าผากเขียวเข้ม ไม่มีลายขีดขาวกระจายที่หัวและคอ เส้นรอบหน้าชัดเจนขึ้นสีฟ้าอ่อน สีเขียวที่ปีกและหลังตอนท้ายเหลือบเป็นมันและแกมสีม่วงมากขึ้น ปากสีน้ำตาลอ่อนกว่าช่วงปกติ, นกวัยอ่อน : สีหม่น ไม่มีเส้นรอบหน้า หัวและคอมีจุดขาวหนาแน่น ขนปีกไม่เหลือบเป็นมันหรือมีเพียงเล็กน้อย', 'แหล่งชุมชนบ้านหัวพลวง และ จังหวัดนครสวรรค์', '4'),
(12, 'นกยางไฟหัวดำ', 'Yellow Bittern', 'Ixobrychus sinensis', 'ตัวผู้ : ปากยาวสีเหลือง หน้าผากถึงท้ายทอยดำตัดกับหัวและลำตัวน้ำตาลอ่อนแกมเหลือง กลางคอด้านหน้าถึงอกขาว มีเส้นยาวน้ำตาลอ่อน หางสั้นมากสีดำ ขณะบินต่างจากชนิดอื่นที่ขนคลุมปลายปีกและขนปีกบินดำ ขนคลุมใต้ปีกขาว, ตัวเมีย : คล้ายตัวผู้ แต่หน้าผากถึงท้ายทอยน้ำตาลดำ กลางคอถึงอกขาว เส้นยาวสีน้ำตาลชัดเจนกว่า, นกวัยอ่อน : คล้ายตัวเมีย กระหม่อมถึงท้ายทอยมีลายขีดดำ กลางคอมีลายยาวสีน้ำตาลเข้ม ลำตัวด้านบนมีลายขีดน้ำตาลเข้ม', 'ตำบลท่าโพธิ์ และ อำเภอเมืองจังหวัดพิษณุโลก', '5'),
(13, 'นกยางไฟธรรมดา', 'Cinnamon Bittern', 'Ixobrychus cinnamomeus', 'ตัวผู้ : ขนลำตัวน้ำตาลแดงสดใสกว่านกยางไฟหัวเทา คอด้านหน้าและลำตัวด้านล่างสีอ่อนกว่า ข้างคอมีแถบขาว ขณะบินต่างจากชนิดอื่นที่ขนน้ำตาลแดงทั้งหมด, ตัวเมีย : หลังและลำตัวด้านบนมีลายจุดจากขอบขนสีน้ำตาลอ่อน ลำตัวด้านล่างน้ำตาลอ่อน คอถึงอกมีขีดประสีน้ำตาลเข้ม, นกวัยอ่อน : คล้ายตัวเมีย แต่ขนลำตัวสีคล้ำกว่า ลายประที่คอและอกดำ', 'วัดบึงราชนก จังหวัดพิษณุโลก', '5'),
(14, 'นกยางดำ', 'Black Bittern', 'Dupetor flavicollis', 'ตัวผู้ : ปากเหลืองคล้ำ ปลายและสันปากบนดำ หัว คอ และลำัตัวด้านบนดำ คอมีแถบยาวสีเหลืองแกมน้ำตาล คอด้านล่างและอกขาวมีลายขีดยาวสีดำหรือแกมน้ำตาลแดงคล้ำ ลำตัวด้านล่างเทาเข้ม แข้งและตีนดำ, ตัวเมีย : หัวและลำตัวด้านบนแกมน้ำตาล มากกว่าตัวผู้ ลายขีดที่คอและอกน้ำตาลแดง, นกวัยอ่อน : คล้ายตัวเมีย แต่หัวและลำตัวด้านบนมีลายเกล็ดจากขอบขนสีน้ำตาล', 'ทุ่งข้าวศรีสัชนาลัย จังหวัดสุโขทัย', '5'),
(15, 'นกยางเขียว', 'Striated Heron', 'Butorides striata', 'ขนาดเล็กกว่านกยางกรอก ตาเหลือง หนังรอบตาและปากเหลืองคล้ำ หัวด้านบนและขนยาวที่ท้ายทอยดำ คอและลำตัวด้านล่างน้ำตาลเทาจางๆ หน้า คอ และกลางอกสีจาง มีแถบหนวดสีคล้ำ ลำตัวด้านบนเทาเข้มแกมเขียวคล้ำ มีลายจากขอบขนสีอ่อน แข้งและตีนเหลือง, นกวัยอ่อน : ขนลำตัวแกมน้ำตาล หัวมีจุดน้ำตาล คอและอกมีลายขีดสีเข้ม ลำตัวด้านบนมีลายจากขอบขนสีอ่อน', 'อุทยานแห่งชาติศรีสัชนาลัย จังหวัดสุโขทัย', '5'),
(16, 'นกแขวก', 'Black-crowned Night heron', 'Nycticorax nycticorax', 'ขนลำตัวเทา หัวและคอแกมขาว กระหม่อม ท้ายทอยและหลังดำ มีขนเปียสีขาว 2 เส้น ขาเหลือง, ฤดูผสมพันธุ์ : ส่วนสีดำมีเหลือบเขียวแกมน้ำเงิน ช่วงจับคู่แข้งและตีนแดง, นกวัยอ่อน : ปากเหลืองคล้ำสันปากดำ หัวและลำตัวด้านล่างน้ำตาล มีลายขีดคล้านนกยางกรอกแต่ตัวใหญ่กว่า ลำตัวด้านบนมีลายจากปลายขนสีขาว', 'ประเทศไทยพบมากทางภาคเหนือ ภาคกลาง และภาคตะวันออกเฉียงเหนือบางส่วน', '5'),
(17, 'นกยางกรอกพันธุ์จีน', 'Chinese Pond Heron', 'Ardeola bacchus', 'นอกฤดูผสมพันธุ์ : ปากเหลืองปลายปากดำ หัว คอ และอกน้ำตาลเหลืองมีลายขีดหนาสีน้ำตาลเข้ม ลำตัวด้านบนน้ำตาลแกมเทาเข้ม ลำตัวด้านล่างขาว แข้งและตีนเหลืองหรือแกมเขียว ขณะบินปีกและหางขาว บางตัวปลายปีกสีคล้ำกว่านกยางกรอกพันธุ์ชวา, ฤดูผสมพันธุ์ : ปากเหลืองสดปลายดำ หนังรอบตาเขียวแกมเหลือง หัว คอ และอกน้ำตาลแดงเข้มแกมเลือดหมู หลังและขนคลุมไหล่เทาดำ แข้งและตีนแดง', 'พบในปากีสถาน, จีน, เกาะไหหลำ, ไต้หวัน, หมู่เกาะอันดามัน, บอร์เนียว, ฮ่องกง และเวียดนาม', '5'),
(18, 'นกยางลายเสือ', 'Malaysian Night Heron', 'Gorsachius melanolophus', 'ปากสั้นสีเหลืองแกมเขียว หนังรอบตาฟ้า กระหม่อมดำ ท้ายทอยมีหงอนยาวสีดำ หน้าและคอสีน้ำตาลแดงแกมส้ม กลางคอและหน้าอกมีขีดดำต่อกันเป็นเส้น ลำตัวด้านบนน้ำตาลเข้มมีลายขวางละเอียดสีดำจางๆ, นกวัยอ่อน : หัวมีลายเกล็ดยาวๆ หนาสีดำ ลำตัวด้านบนน้ำตาลเทามีลายขวางสีเทาสลับดำ มีเส้นประหนาสีดำกลางคอถึงอก', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '5'),
(19, 'นกยางกรอกพันธุ์ชวา', 'Javan Pond Night Heron', 'Ardeola speciosa', 'นอกฤดูผสมพันธุ์ : อาจแยกไม่ได้จากนกยางกรอกชนิดอื่น แต่ปลายปีกอาจสีน้ำตาลแกมเหลืองหรือสีอ่อนกว่านกยางกรอกพันธุ์จีน, ฤดูผสมพันธุ์ : หนังรอบตาแกมเทา หัวน้ำตาลแกมเหลืองนวล คอถึงอกสีค่อยๆ เข้มเป็นน้ำตาลแกมแดง ท้ายทอยมีขนเจ้าชู้ยาวคล้ายเปีย 2 เส้น หลังและขนคลุมไหล่เทาดำ แข้งเหลืองหรือชมพูแกมแดง', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '5'),
(20, 'นกยางเปีย', 'Little Egret', 'Egretta garzetta', 'ปากดำ หนังหน้าเทาหรือเขียวแกมเหลือง ขาดำ ตีนเหลือง โคนปากล่างเหลือง ขนชุดผสมพันธุ์ : หนังหน้าชมพูแกมแดง ท้ายทอยมีขนเปียยาว 1 คู่ หน้าอกและหลังขนเจ้าชู้ยาวเด่นชัด ขาดำสนิทตีนเหลืองส้มหรือบางตัวอาจเป็นสีแดง, นกวัยอ่อน : ขาเทาเข้ม โคนปากหนาและหน้าเทา', 'คลองหนองหลวง จังหวัดพิษณุโลก', '5'),
(21, 'นกยางควาย', 'Eastern Cattle Egret', 'Bubulcus coromandus', 'ขนาดเล็ก หนังที่หน้าเหลืองคล้ำ ปากเหลืองหนาและสั้น หัวโต คอหนา และสั้น ขาค่อนข้างสั้นสีเทาคล้ำ ขนชุดผสมพันธุ์ : ปากเหลืองหรือชมพูสด หัว คอ อก และหลังน้ำตาลแกมส้มสดใส ขนบริเวณท้ายทอย ขนเจ้าชู้ที่อกและหลังยาวมากขึ้น', 'วังเพชร และ จังหวัดพิษณุโลก', '5'),
(22, 'นกยางโทนน้อย', 'Intermediate Egret', 'Ardea intermedia', 'คล้ายนกยางโทนใหญ่ แต่ตัวเล็กกว่า หัวใหญ่กว่า คอหนาและสั้นกว่า หนังหน้าเหลืองปากเหลืองปลายดำ มุมปากลึกระดับเดียวกับตำแหน่งดวงตา ขาและตีนดำ ขนชุดผสมพันธุ์ : หนังหน้าเขียวแกมเหลือง ปากเหลืองสด ปลายปากดำมากขึ้นอาจถึงครึ่งปาก ขนเจ้าชู้ที่อกและหลังยาวมากขึ้น โดยเฉพาะขนอกยาวกว่านกยางโทนใหญ่ ขาและตีนดำ', 'หมู่บ้านวังเพชร จังหวัดพิษณุโลก', '5'),
(23, 'นกยางโทนใหญ่', 'Eastern Great Egret', 'Ardea alba', 'ปากเหลือง หนังหน้าเขียวแกมเหลือง คล้ายนกยางโทนน้อย แต่มุมปากลึกเลยตำแหน่งดวงตาเข้าไป หัวค่อนข้างเล็ก คอเรียวยาว ขนและตีนดำ ขนชุดผสมพันธุ์ : หนังที่หน้าฟ้าเข้ม ปากดำสนิท ขาแดงคล้ำโดยเฉพาะน่อง อกและหลังมีขนเจ้าชู้ยาวออกมา โดยเฉพาะขนหลังจะยาวมาก', 'แอฟริกา, อินเดีย, ญี่ปุ่น, เกาหลี, จีน, ไต้หวันฟิลิปปินส์, ออสเตรเลีย, พม่า, ไทย, ลาว, เขมร,  เวียดนาม ในประเทศไทยพบเกือบทุกภาค เป็นนกอพยพมาในฤดูหนาว', '5'),
(24, 'นกกระสานวล', 'Grey Heron', 'Ardea cinerea', 'ปากเหลือง ขนลำตัวเทา หัวและคอขาว ด้านหน้าคอมีลายขีดดำเป็นเส้นประ หลังตาถึงขนยางคล้ายเปียที่ท้ายทอยสีดำ ขนปีกบินเทาเข้มเกือบดำ ขนชุดผสมพันธุ์ : ปากส้มสด หัวและคอขาวมากขึ้น นกวัยอ่อน : หน้าผาก คอ และลำตัวเทา ปากเหลืองคล้ำ', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '5'),
(25, 'นกกระสาแดง', 'Purple Heron', 'Ardea purpurea', 'ปากเหลือง หัวและคอน้ำตาลแดง มีเส้นดำลากจากหน้าผากถึงท้ายทอย มุมปาก และข้างคอ คอด้านหน้ามีลายประเป็นขีดใหญ่ อกและขนลำตัวด้านบนเทา ขณะบินด้านล่างเทาตัดกับสีเลือดหมูขอบขนคลุมใต้ปีก สีข้าง และท้องด้านท้าย นกวัยอ่อน : ขนลำตัวน้ำตาล ลวดลายที่หัวและคอไม่ชัดเจน', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '5'),
(26, 'นกกาน้ำเล็ก', 'Little Cormorant', 'Microcarbo niger', 'ขนาดเล็กกว่านกกาน้ำชนิดอื่น คอสั้น ปากสั้นสีเทาแกมสีเนื้อ ขนลำตัวน้ำตาลดำ ปีกและขอบขนปีกน้ำตาลเทา ใต้คอขาว ขนชุดผสมพันธุ์ : หัว คอ และลำตัวด้านบนดำเหลือบเขียวเป็นมัน กระหม่อม ขนคลุมหู และท้ายทอยมีขนสีขาวแซมเป็นเส้นเล็ก ๆ ปากดำ', 'หมู่บ้านวังเพชร จังหวัดสุราษฎร์ธานี และ จังหวัดพิษณุโลก', '6'),
(27, 'นกอ้ายงั่ว', 'Oriental Darter', 'Anhinga melanogaster', 'ปากแหลมยาวสีเหลือง คอยาวเรียวเล็ก หัวและคอสีน้ำตาลเข้ม คอด้านหน้าสีอ่อน มีเส้นขาวลากจากตาตามความยาวคอเกือบถึงอก ลำตัวด้านล่างน้ำตาลดำ หลังและปีกมีลายขาวเป็นริ้วจากขนสีขาว หางยาวมีลายบั้งสีขาวแกมเทา ขนชุดผสมพันธุ์ : กระหม่อม หลังคอ หลังตอนบนดำ คอด้านหน้าสีน้ำตาลแดงมากขึ้น', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '7'),
(28, 'เหยี่ยวออสเปร', 'Western Osprey', 'Pandion haliaetus', 'ตัวผู้ : ท้ายทอยมีหงอนสั้น ๆ หัวและลำตัวด้านล่างขาวตัดกับแถบตาและลำตัวด้านบนน้ำตาลเข้ม แถบคาดอกสีน้ำตาล ขณะบินปีกเรียวและยาวมาก ขนคลุมใต้ปีกขาว หัวปีกมีแถบดำใหญ่ ปลายปีกดำ หางบั้งถี่สีเข้ม, ตัวเมีย : แถบคาดอกกว้างกว่า, นกวัยอ่อน : แถบคาดอกไม่ชัดเจน ลำตัวด้านบนน้ำตาลมีลายจากขอบขนสีอ่อน', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '8'),
(29, 'เหยี่ยวกิ้งก่าสีน้ำตาล', 'Jerdon\'s Baza', 'Aviceda jerdoni', 'คล้ายเหยี่ยวภูเขา แต่ขนาดเล็กกว่า ขณะเกาะเห็นหงอนยาวสีดำปลายขาว หัวและท้ายทอยน้ำตาลอ่อน มีแถบดำกลางคอ ลำตัวด้านบนสีน้ำตาลมีลายจากขอบขนสีจาง ลำตัวด้านล่างขาว มีลายขวางสีน้ำตาลแกมส้ม ขณะบินปลายปีกกว้างและมน ขนคลุมใต้ปีกเป็นลายสีน้ำตาลส้ม ใต้ปีกขาว ขนปีกบินมีลายขวางสีดำ หางมีแถบดำ 3 แถบ นกวัยอ่อน : ลำตัวด้านล่างขาวมีลายจุดต่อกันเป็นทางยาวที่ข้างคอและอกตอนบน', 'ถนนช้างทอง และ จังหวัดอุตรดิตถ์', '9'),
(30, 'เหยี่ยวกิ้งก่าสีดำ', 'Black Baza', 'Aviceda leuphotes', 'หงอนยาว หัวและลำตัวดำ อกและท้องขาวมีลายขวางสีดำและน้ำตาลแกมแดง ปีกมีแถบขาวและน้ำตาลแดง ขณะบินปีกสั้น กลางปีกโป่งออกและสอบเรียวไปทางปลายปีก ขนคลุมใต้ปีกดำตัดกับขนปีกบินและหางเทา ขนกลางปีกเทาเกือบดำ ปลายปีกดำ', 'มหาวิทยาลัยเทคโนโลยีราชมงคลล้านนา(วิทยาเขตภาคพายัพ) และ จังหวัดพิษณุโลก', '9'),
(31, 'เหยี่ยวรุ้ง', 'Crested Serpent Eagle', 'Spilornis cheela', 'หนังรอบตาและหนังคลุมจมูกเหลือง ปลายปากดำ ท้ายทอยมีหงอนสั้นๆ ปลายแต้มจุดขาวปกติราบไปกับหัว หัวและลำตัวน้ำตาลเข้ม ลำตัวด้านล่างสีอ่อนกว่า มีจุดขาวขอบดำกระจาย ขณะบินลำตัวด้านล่างและขนคลุมใต้ปีกน้ำตาลเข้มมีลายจุดขาวกระจาย ขนปีกบินดำมีแถบขาวใหญ่พาดกลาง หางดำมีแถบขาวใหญ่พาดกลาง ชนิดย่อย malayensis ทางภาคใต้ ตัวเล็กและสีเข้มกว่าโดยเฉพาะข้างหัวและลำตัวด้านล่าง นกอ่อนวัย : แถบตาใหญ่สีน้ำตาล หัวและลำตัวด้านล่างขาวแกมน้ำตาลอ่อน อกและท้องมีจุดใหญ่กระจาย สีข้างมีลายขวาง หัวและลำตัวด้านบนสีน้ำตาล มีลายเกล็ดจากขอบขนสีอ่อน ปีกมีลายแถบแคบ ๆ สีเข้ม', 'อินเดีย, จีน, ไหหลำ, ไต้หวัน, พม่า, ไทย, อินโดจีน, มาเลเซีย และ ในประเทศไทยพบได้ทุกภาค', '9'),
(32, 'เหยี่ยวผึ้ง', 'Oriental Honey-buzzard', 'Pernis ptilorhynchus', 'แตกต่างจากเหยี่ยวขนาดใหญ่อื่นๆ ที่ปากเรียวเล็ก หัวเล็ก คอค่อนข้างยาว ท้ายทอยมีหงอนสั้นๆ หางยาวปลายมน สีและลวดลายเปลี่ยนแปลงได้หลายแบบ หรือมีการเลียนแบบขนและลวดลายคล้ายเหยี่ยวชนิดอื่น ชนิดย่อย orientails หงอนสั้นมาก ชนิดย่อยประจำถิ่น torquatus บางตัวหงอนที่ท้ายทอยค่อนข้างยาว ตัวผู้สีอ่อน : ตาน้ำตาลแดง หางด้านล่างเห็นแถบขาวกว้างมาก ปลายดำกว้างกว่าตัวเมีย ส่วนใหญ่หัวและหน้าเทา ลำตัวด้านล่างขาวแกมน้ำตาล-น้ำตาลแดง คอขาวมีเส้นดำรอบ อาจมีเส้นกลางคอ ขนคลุมใต้ปีกน้ำตาลเหลือง-น้ำตาลแดง มีลายตามยาวสีเข้ม ขนปีกบินขาวหรือเทา ปลายปีกและขอบปีกด้านหลังดำกว้างกว่าตัวเมีย มีลายตามยาวที่ขนปีกบินด้านใน 2 แถบ, ตัวเมียสีอ่อน : ตาเหลือง แถบขาวใต้หางแคบกว่า เห็นเป็นแถบดำ 4 แถบ แถบปลายหางและแถบขอบปีกด้านหลังแคบกว่าตัวผู้', 'ศูนย์บริการนักท่องเที่ยวอุทยานแห่งชาติแม่วงก์ จังหวัดกำแพงเพชร', '9'),
(33, 'เหยี่ยวแดง', 'Brahminy Kite', 'Haliastur indus', 'ขนลำตัวน้ำตาลแดงสดใส ตัดกับหัวและอกขาวมีลายขีดดำ ขณะบินปีกกว้างปลายปีกแยก ขนคลุมใต้ปีกน้ำตาลแดงเข้ม ขนปีกบินสีอ่อนกว่า มีแถบใหญ่สีน้ำตาลแกมเหลืองจากโคนขนปลายปีก ปลายปีกดำ หนังคลุมจมูก แข้ง และตีนเหลือง นกวัยอ่อน : คล้ายเหยี่ยวดำแต่หางมน ขนลำตัวน้ำตาลแกมดำ มีลายขีดที่หัวและอก ขณะบินใต้ปีกมีแถบขาวแกมน้ำตาลจากโคนขนปลายปีกบิน', 'อินเดีย, จีน, พม่า, เขมร, ลาว, มาเลเซียซุนดาส์, ฟิลิปปินส์, อินโดนีเซีย, ทวีปออสเตรเลีย และประเทศไทยพบได้ทุกภาค', '9'),
(34, 'เหยี่ยวขาว', 'Black-winged Kite', 'Elanus caeruleus', 'หัวและลำตัวด้านล่างขาว ตาแดง แถบตาดำ ลำตัวด้านบนเทาอ่อน เห็นแถบปีกดำตรงจากหัวไหล่ชัดเจน แข้งและตีนเหลือง ขณะบินปีกยาวปลายแหลม หัวไหล่ดำ ขนปลายปีกด้านล่างดำ ปลายขนกลางปีกอาจดำหรือเทาเข้ม มักพบกระพือปีกอยู่กับที่เพื่อมองหาเหยื่อ นกวัยอ่อน : ขนลำตัวแกมน้ำตาล หัวมีลายขีดสีคล้ำ ลำตัวด้านบนมีลายเกล็ดจากขอบปลายขนสีน้ำตาลอ่อน', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '9'),
(35, 'เหยี่ยวหูดำ หรือ เหยี่ยวดำใหญ่', 'Black-eared Kite', 'Milvus lineatus', 'หางเว้าเป็นแฉกน้อยกว่าเหยี่ยวดำ ขนลำตัวน้ำตาลเข้มแต่จางกว่าเหยี่ยวดำ หน้าและคอออกสีขาว ขนคลุมหูสีคล้ำเห็นเป็นแถบชัดเจน ขณะบินใต้ปีกมีแถบสีขาวใหญ่และชัดเจนกว่า หนังคลุมจมูก แข้ง และตีนสีเทาแกมฟ้า นกวัยอ่อน : ลำตัวด้านล่างมีลายขีดสีขาวแกมน้ำตาลชัดเจนกว่าเหยี่ยวดำ หน้ามีแถบดำชัดเจน', 'อำเภอตะพานหิน จังหวัดพิจิตร', '9'),
(36, 'เหยี่ยวด่างดำขาว', 'Pied Harrier', 'Circus melanoleucos', 'ตัวผู้ : ลำตัวด้านล่างขาว ตัดกับหัว อก หลัง และไหล่ ขนคลุมปีกแถวกลางและขนปลายปีกดำ ขณะบินเห็นตะโพกขาว, ตัวเมีย : หัว คอ และอกมีลายขีดสีเข้ม ลำตัวด้านบนน้ำตาล ขณะบินหลังและขนคลุมปีกด้านบนน้ำตาล ขนปีกบินเทามีลายยาวสีเข้ม ตะโพกขาวชัดเจน หางเทาแกมขาวมีลายบั้งสีเข้ม ลำตัวด้านล่างและขนคลุมใต้ปีกขาวมีลายขีดสีเข้ม ขนปีกบินขาวมีแถบดำชัดเจน, นกวัยอ่อน : ลำตัวน้ำตาลเข้มเจือน้ำตาลแดง มีลายตามยาวที่หัวและคอ ท้ายทอยแซมสีขาว ขณะบินด้านบนสีน้ำตาลเข้มค่อนข้างเรียบ ตะโพกขาว หางมีลายบั้งสีเข้ม ลำตัวด้านล่างและขนคลุมใต้ปีกน้ำตาลแกมแดง ปลาปีกค่อนข้างเป็นแถบขาวมีลายแถบดำ', 'จังหวัดพิจิตร และ จังหวัดอุตรดิตถ์', '9'),
(37, 'เหยี่ยวนกเขาหงอน', 'Crested Goshawk', 'Accipiter trivirgatus', 'ตัวผู้ : ตาเหลืองหรือส้ม ท้ายทอยมีหงอนสั้นๆ แต่ปกติราบไปกับหัว แตกต่างจากลำตัวด้านบนสีน้ำตาลแกมเทา คอขาว เส้นกลางคอดำ มีแถบหนวดจางๆสีดำ อกมีลายใหญ่รูปหยดน้ำเรียงกันเป็นขีดหนาสีน้ำตาลแดง ต่อกับลายขวางที่อกด้านล่างและท้อง หางด้านบนมีแถบกว้าง 3 แถบ ขณะบินปีกด้านหลังกว้าง, ตัวเมีย : ขนาดใหญ่กว่า ตาเหลือง หัวเทาแกมน้ำตาล ลายที่อกและท้องสีน้ำตาล, นกวัยอ่อน : คล้ายนกเต็มวัย แต่ตาเหลืองซีด ข้างหัวและลำตัวด้านบนน้ำตาลกว่า ขนคลุมหูมีลายขีดจาง ๆ กระหม่อม ท้ายทอย และลำตัวด้านบนมีลายจากขอบขนสีน้ำตาลอ่อน ลำตัวด้านล่างขาวแกมน้ำตาลอ่อน มีลายขีดยาวสีน้ำตาลเข้มจากคอถึงท้อง ลายขวางมีเฉพาะสีข้างและท้องด้านล่าง', 'อ่างเก็บน้ำแม่สาย และ จังหวัดแพร่', '9'),
(38, 'เหยี่ยวนกเขาพันธุ์จีน', 'Chinese Sparrowhawk', 'Accipiter soloensis', 'ตัวผู้ : ตาแดง หนังคลุมจมูกส้มแกมเหลืองสดกว่าชนิดอื่น ไม่มีเส้นกลางคอ หัวและลำตัวด้านบนเทาแกมฟ้าเข้มกว่าเหยี่ยวนกเขาชิเครา ลำตัวด้านล่างขาว อกสีส้มอ่อนดูเรียบหรือมีลายขวางจางๆ ขณะบินใต้ปีกขาว ปลายปีกดำใหญ่ชัดเจน ขนคลุมใต้ปีกส้มจางๆ,  ตัวเมีย : ตาเหลือง ขนาดใหญ่กว่า มีเส้นกลางคอจางๆ หัวและลำตัวด้านบนแกมน้ำตาลมากกว่าตัวผู้ อกมีลายเข้มกว่า, นกวัยอ่อน : หัวและลำตัวเทาเข้ม ลำตัวด้านบนน้ำตาลเข้ม ลายขีดที่คอและอกน้ำตาลแดงเข้ม หางมีแถบ 4 แถบ ขณะบินใต้หางเห็น 3 แถบ ขนคลุมใต้ปีกน้ำตาลส้มไม่มีลาย ขนปีกบินด้านในมีลายตามยาว 2 แถบ ปลายปีกดำหรือเทาเข้ม', 'เขตรักษาพันธุ์สัตว์ป่าห้วยขาแข้ง จังหวัดอุทัยธานี', '9'),
(39, 'เหยี่ยวนกเขาชิครา', 'Shikra', 'Accipiter badius', 'ตัวผู้ : ตาแดง หัวและลำตัวด้านบนเทาแกมฟ้า ลำตัวด้านล่างขาว คอขาวมีเส้นกลางคอสีเข้ม อกและท้องมีลายขวางถี่สีส้มแกมน้ำตาลแดง หางเทามีลายขวางสีเข้มไม่ชัดเจน ขณะบินปีกตอนในค่อนข้างกว้าง ขนคลุมใต้ปีกมีลายส้มแกมน้ำตาล ขนปีกบินขาวมีลายยาวสีคล้ำ ปลายปีกดำ, ตัวเมีย : ขนาดใหญ่ ตาเหลือง ลำตัวด้านบนแกมน้ำตาลมากกว่าตัวผู้ ลายขวางที่อกหนาแกมน้ำตาล, นกวัยอ่อน : หัวและลำตัวน้ำตาลเข้มมีลายจากขอบขนสีขาว เส้นกลางคอชัดเจน อกมีลายขีดรูปหยดน้ำยาวสีน้ำตาลเข้ม สีข้างเป็นลายขวาง หางน้ำตาลมีแถบสีเข้ม 5 แถบ', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '9'),
(40, 'เหยี่ยวนกเขาพันธุ์ญี่ปุ่น', 'Japanese Sparrowhawk', 'Accipiter gularis', 'ตัวผู้ : ตาแดง คล้ายเหยี่ยวนกกระจอกเล็ก แต่ลำตัวด้านบนสีอ่อน ไม่มีเส้นกลางคอหรือมีแต่จางมาก อกและท้องสีน้ำตาลแกมส้มเข้มกว่าเหยี่ยวนกเขาพันธฺุ์จีน หรือเห็นเป็นลายขวางถี่ๆ แถบหางด้านบนแคบกว่ามักเห็น 4 แถบ, ตัวเมีย : ขนาดใหญ่กว่าตัวผู้ ตาเหลืองหรือแกมส้ม คล้ายเหยี่ยวนกกระจอกเล็ก แต่เส้นกลางคอเล็กกว่า อกไม่มีลายขีด ลายขวางที่อก ท้อง และสีข้างละเอียดกว่า สีน้ำตาลเข้มแกมเทา สีหัวและหลังไม่ต่างกันมาก, นกวัยอ่อน : ยากที่จะแยกจากเหยี่ยวนกกระจอกเล็ก แต่เส้นกลางคอบางกว่า ขีดที่อกน้อยกว่าและไม่เป็นสีดำ', 'ศูนย์วิจัยและพัฒนาการเพาะเลี้ยงสัตว์น้ำจืดสุโขทัย และ อุทยานประวัติศาสตร์ศรีสัชนาลัยจังหวัดสุโขทัย', '9'),
(41, 'เหยี่ยวปีกแดง', 'Rufous-winged Buzzard', 'Butastur liventer', 'ตัวผู้ : คล้ายเหยี่ยวหน้าเทา ตาเหลือง หนังคลุมจมูกเหลืองส้มสด หัวและลำตัวเทา ลำตัวด้านบนน้ำตาลแดงสดใส แข้งยาวสีเหลืองส้ม ขณะบินขนปีกบินและหางน้ำตาลแดงชัดเจน ขนคลุมใต้ปีกขาว, นกวัยอ่อน : คิ้วขาวแคบๆ หัวและลำตัวด้านล่างน้ำตาล ลำตัวด้านบนแกมน้ำตาลแดง แถบกลางปีกน้ำตาลแดง', 'อำเภอโพธิ์ประทับช้าง จังหวัดพิจิตร', '9'),
(42, 'เหยี่ยวหน้าเทา', 'Grey-faced Buzzard', 'Butastur indicus', 'ตัวผู้ : ตาและขนคลุมจมูกเหลือง หัวเทาอาจมีคิ้วขาว ท้ายทอยมีลายขีดขาวเป็นแถบ คอขาว มีเส้นกลางคอและมีแถบหนวดเป็นขอบด้านข้างสีเทาเข้มเกือบดำ อกน้ำตาลมีลายขีดยาวสีเข้ม อกตอนล่างและท้องมีลายขวางสั้นๆ ต่อกันเป็นลายยาว ลำตัวด้านบนน้ำตาลแกมเทา แข้งยาวสีเหลือง ขณะบินคล้ายเหยี่ยวสกุล Accipiter แต่ปีกยาวเรียวกว่า ปลายค่อนข้างแหลม ขนปีกบินน้ำตาลแกมแดง ใต้ปีกขาวปลายปีกดำ ขนคลุมใต้ปีกมีลายขีดสีน้ำตาลเล็กๆ หนาแน่น ขนปีกบินมีลายเทาดำจางๆ หางมีแถบดำ 3 แถบ, ตัวเมีย : คิ้วขาว ชัดเจนกว่า หัวน้ำตาลกว่า อกมีลายขวางสีขาวมากกว่า, นกวัยอ่อน : หัวและคอน้ำตาลมีลายขีดขาว คิ้วขาวแกมน้ำตาลชัดเจน แถบหน้าดำแกมน้ำตาล ลำตัวด้านล่างขาวมีลายขีดน้ำตาลเข้ม', 'อุทยานแห่งชาติตากสินมหาราช จังหวัดตาก', '9'),
(43, 'เหยี่ยวเคสเตรล', 'Common Kestrel', 'Falco tinnunculus', 'ตัวผู้ : หัวเทา หนังรอบตาและหนังคลมจมูกเหลือง แถบหนวดข้างแก้มดำ ลำตัวด้านบนน้ำตาลแดงมีลายจุดจากปลายขนดำ ตะโพกและหางเทาปลายดำ ขอบหางขาว ลำตัวด้านล่างน้ำตาลอ่อนแกมขาวมีลายจุดสีเข้ม ขณะบินด้านบนเห็นขนปลายปีกดำ ปีกด้านล่างขาวแกมน้ำตาลมีลายดำที่ขนคลุมใต้ปีก, ตัวเมีย : หัวและลำตัวด้านล่างน้ำตาลแดงมีลายขีดดำชัดเจนกว่า หางน้ำตาลแดงมีลายบั้งเล็กๆ ปลายดำกว้างปลายสุดขาว ท้องและก้นค่อนข้างขาว, นกวัยอ่อน : คล้ายตัวเมียแต่ลายขีดและจุดชัดเจนกว่า ลำตัวด้านบนแกมน้ำตาลมากกว่า', 'ทุ่งทะเลหลวง จังหวัดสุโขทัย', '10'),
(44, 'เหยี่ยวฮอบบี้ยุโรป', 'Eurasian Hobby', 'Falco subbuteo', 'คล้ายเหยี่ยวเพเรกรินขนาดเล็ก หนังรอบตาและหนังคลุมจมูกเหลือง มีคิ้วขาวแคบ ๆ แถบหนวดข้างแก้มดำตัดกับแก้มและข้างคอขาว ลำตัวด้านล่างขาวมีลายขีดหนา ท้องและลำตัวด้านล่างสีน้ำตาลแดง แข้งและตีนเหลือง ขณะบินปีกแหลมและเรียวกว่าเหยี่ยวตีนแดง มีลายขีดที่อกถึงท้อง หางดำมีบั้งขาว นกไม่เต็มวัย : หัวและลำตัวด้านบนน้ำตาลแกมเทา มีลายเกล็ดจากขอบขนสีจาง ท้องและก้นน้ำตาลแกมเหลือง ขณะบินรูปทรงปีกต่างจากเหยี่ยวตีนแดง มีลายขีดจากอกถึงท้อง แข้งและตีนเหลือง', 'อำเภอตะพานหิน จังหวัดพิจิตร', '10'),
(45, 'เหยี่ยวเพเรกริน', 'Peregrine Falcon', 'Falco peregrinus', 'หัวและแถบหนวดดำตัดกับข้างแก้มและคอสีขาวเป็นแถบใหญ่ ชนิดย่อยอพยพ japonensis ลำตัวด้านบนเทาเข้ม ลำตัวด้านล่างขาว อกมีลายจุดเล็กๆ ลำตัวด้านล่างมีลายละเอียดต่อกันตามขวาง ขณะบินปีกกว้าง ปลายมนกว่าชนิดอื่นๆ ใต้ปีกขาวมีลายดำหนาแน่นที่ขนคลุมใต้ปีก ชนิดย่อยอพยพ peregrinator ลำตัวด้านล่างแกมน้ำตาลแดง ชนิดย่อยประจำถิ่นภาคใต้ ernesti แก้มดำ หัวและลำตัวสีคล้ำมาก อกค่อนข้างเรียบ มีลายขวางหนาแน่นที่ท้องทำให้ลำตัวด้านล่างเป็นสีดำ นกวัยอ่อน : มีลายขีดที่อกและท้อง ลำตัวด้านบนแกมน้ำตาลมากกว่านกเต็มวัย ชนิดย่อย peregrinator อกและท้องลาย แซมด้วยสีน้ำตาลแดงถึงท้องด้านล่าง', 'อำเภอตะพานหิน จังหวัดพิจิตร', '10'),
(46, 'นกอัญชันป่าขาเทา', 'Slaty-legged Crake', 'Rallina eurizonoides', 'คล้ายนกอัญชันป่าขาแดง แต่ขนาดใหญ่กว่า ปากหนาและยาวกว่า ปีกไม่มีลายขวาง แข้งและตีนสีเทา ปีกและหลังสีน้ำตาลคล้ำมากกว่า', 'อุทยานแห่งชาติศรีสัชนาลัยจังหวัดสุโขทัย', '11'),
(47, 'นกอัญชันอกสีเทา', 'Slaty-breasted Rail', 'Gallirallus striatus', 'ปากยาวสีแดงคล้ำ หน้าผากถึงท้ายทอยน้ำตาลแดง หน้า คอ อก และท้องเทา ลำตัวด้านบนน้ำตาลแกมเทามีลายขาว ท้องถึงก้นเป็นลายขวางขาวดำ ขาและตีนเทาดำ นกวัยอ่อน : หัวและอกน้ำตาล หลังมีลายขีดสีดำสลับขาว', 'บึงกะโล่ จังหวัดอุตรดิตถ์', '11'),
(48, 'นกกวัก', 'White-breasted Waterhen', 'Amaurornis phoenicurus', 'ปากเหลือง โคนสันปากบนแดง หน้า คอตอนหน้า อก และท้องขาว กระหม่อมถึงหลังดำแกมเทา ท้องตอนล่างถึงก้นน้ำตาลแดง แข้งและตีนเหลือง นกวัยอ่อน : ลำตัวด้านบนเทาดำแกมน้ำตาล ลำตัวด้านล่างขาวหม่น', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '11'),
(49, 'นกอัญชันเล็ก', 'Baillon\'s Crake', 'Porzana pusilla', 'ขนาดเล็กมาก ปากสั้นสีเขียว หน้า คอด้านหน้า อก และท้องเทาแกมฟ้า กระหม่อม ท้ายทอย และลำตัวด้านบนน้ำตาลแดง มีลายเกล็ดดำ แซมจุดขาว ท้องถึงก้นมีลายขวางขาวสลับดำ แข้งและตีนเขียว นกวัยอ่อน : สีหม่นกว่า หน้า คอ และอกขาวแกมเทา', 'หุบเขาทะเลแก้ว จังหวัดพิษณุโลก', '11'),
(50, 'นกอัญชันคิ้วขาว', 'White-browed Crake', 'Porzana cinerea', 'ขนาดเล็กมาก ปากเหลืองโคนปากแดง โคนปากล่างและแถบตาดำ มีแถบขาวคล้ายคิ้วและแถบขาวที่มุมปากถึงข้างแก้ม หัว คอ อก และท้องเทาแกมขาว ลำตัวด้านบนน้ำตาลแกมเหลืองมีลายเกล็ดน้ำตาลดำ ท้องตอนล่างถึงก้นน้ำตาลแกมเหลือง แข้งและตีนเหลืองแกมเขียว', 'ตำบลท่าโพธิ์ จังหวัดพิษณุโลก', '11'),
(51, 'นกหนูแดง', 'Ruddy-breasted Crake', 'Porzana fusca', 'ลำตัวด้านบนสีน้ำตาลแกมเขียวไพล หน้า คอด้านหน้าและด้านข้าง อก และท้องตอนบนแดงเลือดหมูแกมน้ำตาล ท้องตอนล่างถึงก้น มีลายขวางขาวสลับดำ แข้งและตีนแดงคล้ำ นกวัยอ่อน : ลำตัวด้านล่างขาวสลับลายเทาดำ', 'คลองหนองหลวง จังหวัดพิษณุโลก', '11'),
(52, 'นกอีโก้ง', 'Grey-headed Swamphen', 'Porphyrio porphyrio', 'หัวและลำตัวสีน้ำเงินแกมม่วง ปากอวบใหญ่สีแดง โคนสันปากมีกระบังสีแดงสด ชนิดย่อย policocephalus (ภาคเหนือลงมาถึงจังหวัดนครสวรรค์) หน้าและหัวสีเทาแกมขาว คอ อกตอนบน หลัง และปีกสีน้ำเงินคล้ำแกมน้ำตาล ก้นและขนคลุมใต้หางขาว แข้งและตีนแดงสด ในขณะที่ ชนิดย่อย viridis (ตั้งแต่นครสวรรค์ลงไป) ขนาดเล็กกว่า ลำตัวด้านบนสีดำแกมน้ำตาล นกวัยอ่อน : สีคล้ำกว่า ปากดำ', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '11'),
(53, 'นกอีลุ้ม', 'Watercock', 'Gallicrex cinerea', 'ตัวเมียและตัวผู้นอกฤดูผสมพันธุ์ : รูปร่างเพรียว ปากเหลือง คอยาว ขนลำตัวสีน้ำตาลแกมเหลือง หลังและปีกเป็นลายเกล็ดขนาดใหญ่เกิดจากขนสีน้ำตาลเข้มมีขอบน้ำตาลเหลือง ท้องมีลายขวางจางๆ ชัดเจนกว่าในตัวผู้ แข้งและตีนเหลืองแกมเขียว', 'บึงน้ำใส จังหวัดนครสวรรค์', '11'),
(54, 'นกอีล้ำ', 'Common Moorhen', 'Gallinula chloropus', 'ปากแดงปลายเหลือง โคนสันปากบนเป็นกระบังสีเดียวกับปาก ตาน้ำตาลเข้ม ขนลำตัวดำอกดำแกมเทา หลังดำแกมน้ำตาล มีขีดขาวเป็นเส้นๆ ตามยาวข้างลำตัว ก้นและขนคลุมใต้หางขาวมีแถบดำตรงกลาง นกวัยอ่อน : ปากเหลืองแกมเขียว คอด้านหน้า อก และท้องขาวแกมเทา ลำตัวสีน้ำตาลแกมเทา ใต้หางขาว', 'คลองหนองหลวง จังหวัดพิษณุโลก', '11'),
(55, 'นกคู้ต', 'Eurasian Coot', 'Fulica atra', 'ปากและกระบังโคนสันปากบนขาว ตาสีน้ำตาล ขนลำตัวดำ ลำตัวด้านล่างดำแกมเทา แข้งและตีนเขียว นกวัยอ่อน : กระบังโคนสันปากเล็ก ขนลำตัวเทาแกมน้ำตาล หน้า คอ และอกขาวแกมเทา', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '11'),
(56, 'นกคุ่มอกลาย', 'Barred Buttonquail', 'Turnix suscitator', 'ปากและขาสีเทา ขณะบินเห็นท้ายลำตัวด้านใต้สีส้ม ตัวผู้ : หน้าสีเทา มีลายขวางถี่ ๆ พาดอก หลังสีน้ำตาลเข้มมีลายขีดสีเนื้อและจุดดำกระจาย สีข้าง ท้อง และก้นสีน้ำตาลแกมส้ม, ตัวเมีย : คอและอกสีดำ ขนลำตัวสีน้ำตาลแดงมากกว่า', 'อุทยานแห่งชาติศรีสัชนาลัย จังหวัดสุโขทัย', '12'),
(57, 'นกตีนเทียน', 'Black-winged Stilt', 'Himantopus himantopus', 'ตัวผู้ : ปากบาง แหลมและยาวตรงสีดำ หัวและลำตัวขาว ปีกและลำตัวด้านบนดำ ขายาวมากสีชมพูแดง บางตัวอาจมีสีดำที่หัวและท้ายทอยรูปแบบแตกต่างกัน, ตัวเมีย : ลำตัวด้านบนดำแกมน้ำตาล หัวและท้ายทอยอาจมีแถบสีเทา, นกวัยอ่อน : กระหม่อมและท้ายทอยน้ำตาลแกมเทา ลำตัวด้านบนและปีกน้ำตาลแกมเทามีลายเกล็ดจากขอบบนสีน้ำตาลอ่อน', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '13'),
(58, 'นกกระแตหัวเทา', 'Grey-headed Lapwing', 'Vanellus cinereus', 'ปาเกเหลืองส้มปลายดำ หนังรอบตาและหนังเล็กๆ ที่หน้าผากเหลือง หัวถึงอกเทามีแถบดำคาดอกตอนล่าง ลำตัวด้านบนน้ำตาล ลำตัวด้านล่างขาว แข้งและตีนเหลืองส้ม ขณะบินปีกน้ำตาล กลางปีกขาว ขนปลายปีกดำ ตะโพกและหางขาว ปลายหางดำ', 'คลองหนองเหล็ก จังหวัดพิษณุโลก', '14'),
(59, 'นกหัวโตหลังจุดสีทอง', 'Pacific Golden Plover', 'Pluvialis fulva', 'ขนาดและหัวเล็กกว่านกโตสีเทา ปากบางกว่า ขนลำตัวสีน้ำตาลเหลือง หลังน้ำตาลเข้ม มีลายจุดกระจายจากขอบขนสีน้ำตาลเหลืองและขาว ขนชุดผสมพันธุ์ : คล้ายนกหัวโตสีเทาฤดูผสมพันธุ์ แต่หลังดำมีจุดสีน้ำตาลเหลืองจากขอบขนสีอ่อนกระจายหนาแน่น แซมด้วยจุดขาว', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '14'),
(60, 'นกหัวโตเล็กขาเหลือง', 'Little Ringed Plover', 'Charadrius dubius', 'ขนาดเล็ก ปากสั้นสีดำ หนังรอบตาหนาสีเหลืองชัดเจน หน้าผากและคิ้วขาวแกมน้ำตาลอ่อน แถบหน้าผากไม่ชัดเจน รอบคอขาวต่อด้วยแถบรอบคอสีน้ำตาลเข้ม หนาและกว้าง โดยเฉพาะบริเวณอก หัวและลำตัวด้านบนน้ำตาล ขนชุดผสมพันธุ์ : หนังรอบตากว้างสีเหลืองสด หน้าผาก คิ้ว และรอบคอขาว แถบหน้าผาก แถบตา และแถบคาดอกดำเข้ม แข้งและตีนเหลืองสดหรือเหลืองแกมส้ม', 'หมู่บ้านวังเพชร จังหวัดพิษณุโลก', '14'),
(61, 'นกหัวโตขาดำ', 'Kentish Plover', 'Charadrius alexandrinus', 'ปากดำ แข้งและตีนดำหรือเทาเข้มเกือบดำ หน้าผากและคิ้วขาว หัว แถบตา แถบข้างอก และลำตัวด้านบนน้ำตาล รอบคอและลำตัวด้านล่างขาว  ตัวผู้ขนชุดผสมพันธุ์ : ส่วนสีขาวสะอาดขึ้น กระหม่อมถึงท้ายทอยน้ำตาลแดง แถบหน้าผาก แถบตา และแถบข้างอกดำ, ตัวเมียขนชุดผสมพันธุ์ : หัวไม่สดเท่าตัวผู้ แถบหน้าผาก แถบตาและแถบข้างอกสีน้ำตาลเข้ม', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '14'),
(62, 'นกหัวโตทรายเล็ก', 'Lesser Sand Plover', 'Charadrius mongolus', 'ความยาวน่องสั้นกว่าแข้ง หัวและลำตัวด้านบนน้ำตาล ไม่มีรอบคอขาว แถบข้างอกน้ำตาล หน้าผากและคิ้วขาว ลำตัวด้านล่างขาว แข้งและตีนเหลืองคล้ำ ตัวผู้ขนชุดผสมพันธุ์ : หน้าผาก แถบคาดตาถึงขนคลุมหูดำ หัวด้านบน ข้างคอและแถบคาดอกส้มแกมน้ำตาล ด้านบนแถบอกมีเส้นดำเล็กจางๆ ชนิดย่อย mongolus มีแถบขาวที่หน้าผาก แบ่งครึ่งกลางด้วยเส้นดำ เส้นดำเหนือแถบอกใหญ่และชัดเจนกว่า, ตัวเมียขนชุดผสมพันธุ์ : คล้ายตัวผู้แต่สีซีดกว่า หน้าผากและแถบคาดตาน้ำตาลเข้ม', 'คลองหนองหลวง จังหวัดพิษณุโลก', '14'),
(63, 'นกโป่งวิด', 'Greater Painted-snipe', 'Rostratula benghalensis', 'ตัวผู้  : คล้ายนกปากซ่อม แต่ปากสั้นกว่าปลายโค้งลง แถบกลางหัว แถบตา และหนังรอบตาสีน้ำตาลเหลือง หัว อก และหลังน้ำตาลเข้มแกมเทา ปีกและลำตัวด้านบนมีลายสีน้ำตาลสลับน้ำตาลเข้ม ลำตัวด้านล่างขาว, ตัวเมีย : แถบกลางหัวน้ำตาลเหลือง หัวตอนบนดำตัดกับแถบตาและวงรอบขาว หัว คอ อก และหลังตอนบนน้ำตาลแดงแกมเลือดหมู อกด้านล่างมีแถบดำต่อเนื่องกับลำตัวด้านล่างสีขาว ปีกน้ำตาลเข้ม', 'อำเภอตะพานหิน จังหวัดพิจิตร', '15'),
(64, 'นกอีแจว', 'Pheasant-tailed Jacana', 'Hydrophasianus chirurgus', 'หน้าผากถึงท้ายทอยน้ำตาลเข้ม หน้าและคอขาวมีแถบตาต่อเนื่องถึงแถบอกสีดำ ข้างคอเหลือง ลำตัวด้านบนน้ำตาล ลำตัวด้านล่างขาว คล้ายนกพริกไม่เต็มวัย แต่ขณะบินปีกขาวปลายปีกดำ ขนชุดผสมพันธุ์ : หัวและหน้าขาว กระหม่อมดำต่อเนื่องกับเส้นข้างคอ ลำตัวน้ำตาลดำ แถบปีกขาว หางยาวขึ้นอย่างน้อย 25 ซม.', 'ตำบลท่าโพธิ์ และ อำเภอเมืองจังหวัดพิษณุโลก', '16'),
(65, 'นกพริก', 'Bronze-winged Jacana', 'Metopidius indicus', 'ปากเหลืองสด สันปากบนเทาแกมฟ้า โคนปากบนแดง คิ้วขาวตัดกับหัว คอและลำตัวด้านล่างสีดำเหลือบเป็นมัน หลังตอนบนดำเหลือบม่วง ปีกและลำตัวด้านบบนน้ำตาลแกมสีทองแดง หลัง ตะโพก หาง และก้นสีน้ำตาลแดง แข้งและตีนเขียว นกวัยอ่อน : หน้าผากและหัวด้านบนสีน้ำตาลแดง แถบตาน้ำตาลเข้ม ท้ายทอยและหลังคอน้ำตาลเข้มเกือบดำ คอด้านหน้าและลัตวด้านล่างขาว มีแถบน้ำตาลเหลืองที่คอด้านบนและอก', 'บึงกะโล่ จังหวัดอุตรดิตถ์', '16'),
(66, 'นกปากซ่อมหางเข็ม', 'Pintail Snipe', 'Gallinago stenura', 'ปากสั้นกว่านกปากซ่อมหางพัด แต่ขณะบินขนคลุมใต้ปีกมีลายขวางตลอด ขอบขนกลางปีกไม่ขาว ท้องขาว ลักษณะทั่วไปคล้ายนกปากซ่อมหางพัดมาก แต่สีลำตัวน้ำตาลอ่อนกว่าเล็กน้อย หางสั้นสีน้ำตาลแดงปลายดำขาว ขณะยืนขนโคนปีกคลุมขนปลายปีกไว้และเกือบคลุมปลายหาง ขนหางมี 24 - 28 ขน ขนหาง 6 - 9 ขน จากด้านนอกแคบเรียวเล็กกว่าขนอื่น', 'อำเภอตะพานหิน จังหวัดพิจิตร', '17'),
(67, 'นกปากซ่อมหางพัด', 'Common Snipe', 'Gallinago gallinago', 'ปากยาวกว่านกปากซ่อมหางเข็ม ขณะยืนปลายหางยาวกว่าปลายปีกมากกว่าชนิดอื่น ขณะบินจำแนกจากชนิดอื่นที่ใต้ปีกมีแถบขาวบริเวณขนคลุมใต้ปีกและขอบขนกลางปีกขาว ท้องขาวมากกว่านกปากซ่อมหางเข็ม ขนหางมี 12 - 18 ขน แต่ละขนขนาดใกล้เคียงกัน', 'นาข้าวศรีสัชนาลัย จังหวัดสุโขทัย', '17'),
(68, 'นกทะเลขาแดงลายจุด', 'Spotted Redshank', 'Tringa erythropus', 'ขาแดงและยาวกว่านกทะเลขาแดงธรรมดา ปากดำเรียวและยาวกว่า ปลายปากโค้งเล็กน้อย โคนปากล่างแดงเข้ม คิ้วขาวแถบตาดำชัดเจน ลำตัวด้านบนเทาอ่อน ขณะบินหลังตอนล่าง ตะโพก และหางขาว หางมีลายบั้งน้ำตาลจาง ๆ ชุดขนผสมพันธุ์ : ขนลำตัวดำแกมเทา ลำตัวด้านบนมีลายจุดจากขอบขนขาว', 'ตำบลวัดโบสถ์ จังหวัดพิษณุโลก', '17'),
(69, 'นกทะเลขาแดง', 'Common Redshank', 'Tringa totanus', 'ขาแดง คล้ายนกทะเลขาแดงลายจุด แต่ปากตรงหนากว่า โคนปากบนและล่างแดงปลายดำ ลำตัวด้านบนน้ำตาลเข้มกว่า คิ้วขาวไม่ชัดเจนเท่า คอและอกมีลายขีดสีเข้ม ท้องขาว ขณะบินขอบขนปีกบินด้านในและขนปลายปีกคู่ในๆขาว หลังตอนล่างและตะโพกขาว หางขาวมีลายขวางสีน้ำตาลชัดเจน ขนชุดผสมพันธุ์ : ลำตัวด้านบนสีน้ำตาลแกมส้ม ลายที่คอ อกและท้องหนาและชัดเจนมากขึ้น', 'อำเภอตะพานหิน จังหวัดพิจิตร', '17'),
(70, 'นกทะเลขาเขียวธรรมดา', 'Common Greenshank', 'Tringa nebularia', 'คล้ายนกชายเลนบึง แต่ขนาดตัวใหญ่ ขาหนากว่า ปากหนาแอ่นขึ้นเล็กน้อย โคนปากหนาสีเทาแกมเขียวปลายดำ หน้าผากขาว หัวและหลังคอมีลายขีดจางๆ ลำตัวด้านบนเทามีลายจุดจากขอบขนสีเข้ม ขาและตีนเขียวแกมเทา ขณะบินตีนยื่นยาวเลยปลายหาง หลังตอนล่าง ตะโพก และหางขาว ปลายหางมีลายขวางสีน้ำตาล ขนชุดผสมพันธุ์ : หน้าผาก หัว คอ และอกมีลายชัดเจนขึ้น', 'คลองหนองหลวง จังหวัดพิษณุโลก', '17'),
(71, 'นกชายเลนปากโค้ง', 'Curlew Sandpiper', 'Calidris ferruginea', 'ปากสีดำ ยาวโค้งลง หัวและลำตัวเทาแกมน้ำตาล คิ้วยาวสีขาว ลำตัวด้านล่างขาว ขณะบินปีกมีแถบขาวเล็กๆ แตกต่างจากนกชายเลนท้องดำที่ตะโพกขาว หางดำ แข้งและตีนดำ ขนชุดผสมพันธุ์ :  หัวและลำตัวด้านล่างสีน้ำตาลแดงเข้ม คิ้วยาวสีขาวแกมน้ำตาลอ่อน หลังน้ำตาลแดงแซมเทาและจุดดำ', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '17'),
(72, 'นกชายเลนบึง', 'Marsh Sandpiper', 'Tringa stagnatilis', 'ขายาวเรียวสีเขียวหรือเหลือง คล้ายนกทะเลขาเขียว แต่ขนาดเล็กกว่า ปากยาวแหลมและบางกว่า หัว ท้ายทอย หลังคอ และลำตัวด้านบนเทา คิ้วค่อนข้างยาวสีขาว คอด้านหน้าและลำตัวด้านล่างขาว ขณะบิน หลังตอนล่าง ตะโพก และหางขาว มีลายขวางสีน้ำตาลจางๆที่หาง ขนชุดผสมพันธุ์ :  หัว หลังคอ และอกมีลายสีเข้ม หลังเทาแกมขาว มีลายจุดสีเข้มชัดเจน', 'บึงราชนก จังหวัดพิษณุโลก', '17'),
(73, 'นกชายเลนน้ำจืด', 'Wood Sandpiper', 'Tringa glareola', 'คล้ายนกชายเลนเขียว แต่คิ้วยาวสีขาวชัดเจน อกสีน้ำตาลมีลายขีดจางๆ หลังน้ำตาลเข้มมีลายจุดสีอ่อนกระจาย ขาเหลืองหรือเหลืองแกมส้ม ขณะบินตะโพกและหางขาวมีลายขวาง สีน้ำตาลเข้มเกือบดำ แต่เรียวกว่านกชายเลนเขียว', 'คลองหนองหลวง จังหวัดพิษณุโลก', '17'),
(74, 'นกชายเลนเขียว', 'Green Sandpiper', 'Tringa ochropus', 'ขาค่อนข้างสั้นสีเขียวคล้ำหรือเขียวแกมเหลือง หัวและอกสีน้ำตาลแกมเทา ตัดกับอกด้านล่างและท้องขาว หัวตาขาวหรือเป็นคิ้วสั้นๆ ลำตัวด้านบนสีน้ำตาลเข้ม ขณะยืนปลายปีกยาวเสมอปลายหางหรือยาวกว่าเล็กน้อย ขณะบินเห็นหางขาวมีลายพาดดำหนา 3 - 4 แถบที่ปลายหาง', 'คลองหนองหลวง จังหวัดพิษณุโลก', '17'),
(75, 'นกเด้าดิน', 'Common Sandpiper', 'Actitis hypoleucos', 'ขณะยืนและเดินมักกระดกก้นและหางขึ้นลง ปลายหางยาวกว่าปลายปีกมาก หัวและลำตัวด้านบนสีน้ำตาล คิ้วยาวสีชัดเจน ข้างอกทั้งสองข้างมีแถบน้ำตาลขนาดใหญ่ ลำตัวด้านล่างขาว ขาและตีนเหลือง ขณะบินมีแถบขาวชัดเจนที่ปีก หัวไหล่มีแถบน้ำตาลเข้ม ตะโพกและหางน้ำตาลมีลายขาวที่ขอบและปลายหาง', 'ศูนย์วิจัยและพัฒนาการเพาะเลี้ยงสัตว์น้ำจืดสุโขทัย', '17'),
(76, 'นกสติ๊นท์คอแดง', 'Red-necked Stint', 'Calidris ruficollis', 'ปากสั้นตรง คอสั้น ลำตัวด้านบนเทาแกมน้ำตาลอ่อน บางตัวอาจเห็นลายขีดบนหลังจากแกนเส้นสีเข้ม และลายเกล็ดจากขอบขนสีจาง คิ้วยาวสีขาว แถบตาสีเข้ม ลำตัวด้านล่างขาว อกเทาอ่อน แข้งและตีนดำ ชุดขนฤดูผสมพันธุ์ : หัว อก และหลังน้ำตาลแดงเข้ม ขนปีกเทา', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '17'),
(77, 'นกสติ๊นท์อกเทา', 'Temminck\'s Stint', 'Calidris temminckii', 'คล้ายนกสติ๊นท์คอแดง แต่แข้งและตีนเหลือง คิ้วยาวสีขาว หัวและหลังน้ำตาลแกมเทา อกเทา ลำตัวด้านล่างขาว  ขนชุดผสมพันธุ์ : หลังมีลายดำจากแกนขนสีเข้ม', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '17'),
(78, 'นกลอยทะเลคอแดง', 'Red-necked Phalarope', 'Phalaropus lobatus', 'ปากบางตรงและแหลม แถบตาดำ กระหม่อม ท้ายทอย และหลังเทาอ่อน ลำตัวด้านบนเห็นเป็นลายเกล็ดบางๆจากขอบขนสีอ่อน ลำตัวด้านล่างขาว ตัวเมียขนชุดผสมพันธุ์ : หัวและลำตัวเทาเข้มเกือบดำ คางและคอขาว ข้างหัว ข้างคอถึงคอด้านบนน้ำตาลแดงเป็นแถบยาว หลังมีแถบยาวสีน้ำตาลเหลือง สีข้างมีลายขีดเทา, ตัวผู้ขนชุดผสมพันธุ์ : คล้ายตัวเมียแต่สีซีดกว่า, นกวัยอ่อน : กระหม่อมและท้ายทอยน้ำตาลแกมดำ ลำตัวด้านบนมีลายเกล็ดจากขอบขนน้ำตาลเหลือง อกและข้างคอน้ำตาลเหลือง', 'อำเภอเมืองจังหวัดตาก', '17'),
(79, 'นกแอ่นทุ่งใหญ่', 'Oriental Pratincole', 'Glareola maldivarum', 'ปากสั้นสีแดงปลายดำ หัว คอ และอกน้ำตาลอ่อนแกมเทา ใต้คอเหลืองอ่อมแกมน้ำตาลมีเส้นประดำต่อเนื่องกับมาถึงใต้ตา ท้องตอนล่างและก้นขาว ขณะบินคล้ายนกนางแอ่น ใต้ปีกแดงแกมน้ำตาลเจ้ม ขนปีกบินดำ ตะโพกขาว หางแฉกสีดำ ขนชุดผสมพันธุ์ : โคนปากแดงสด สีสดใสและเส้นดำใต้คอชัดเจนขึ้น, นกวัยอ่อน : ไม่มีเส้นสีดำล้อมรอบใต้คอสีขาว ขนลำตัวมีลายจุดน้ำตาลแดงและขาว', 'คลองหนองหลวง จังหวัดพิษณุโลก', '18'),
(80, 'นกนางนวลแกลบเคราขาว', 'Whiskered Tern', 'Chlidonias hybrida', 'ปีกกว้างและสั้น ปากหนาสั้นกว่าสกุล sterna ปากดำ แถบตาดำ กระหม่อมมีลายขีดจนเป็นแถบดำที่ท้ายทอย แข้งและตีนแดงเข้ม หางเว้าตื้น ขนชุดผสมพันธุ์ : ปากแดงเข้มเกือบดำ หน้าผากถึงท้ายทอยดำ ข้างแก้มขาว ตัดกับลำตัวด้านล่างเทาเข้มหรือเกือบดำ นกวัยอ่อน : ลำตัวด้านบนมีลายแถบจากขนสีน้ำตาลเข้ม', 'หนองจรเข้ จังหวัดสุโขทัย', '19'),
(81, 'นกนางนวลแกลบดำปีกขาว', 'White-winged Black Tern', 'Chlidonias leucopterus', 'คล้ายนกนางนวลแกลบเคราขาว แต่ขนาดเล็กกว่า ปากบางกว่า ขนคลุมหูดำกลมเด่นชัด กระหม่อมและท้ายทอยดำ ขณะบินมักเห็นแถบใต้ปีกจากขอบขนคลุมใต้ปีกสีดำ ตะโพก และหางขาว ขนชุดผสมพันธุ์ : ปากแดงคล้ำ หัว ลำตัว และใต้ปีกดำ ตัดกับขอบขนปีกบินด้านหลัง ก้น ปีกด้านบน และหางขาวแกมเทา, นกวัยอ่อน : ลำตัวด้านบนมีลายจากขนสีน้ำตาลและดำ ', 'หนองจรเข้ จังหวัดสุโขทัย', '19'),
(82, 'นกนางนวลขอบปีกขาว', 'Black-headed Gull', 'Chroicocephalus ridibundus', 'คล้ายนกนางนวลธรรมดา แต่ขนาดเล็กกว่าและตาสีน้ำตาลเข้มทุกช่วงอายุ ปากบางกว่าเล็กน้อยสีแดงเข้มปลายดำ ขนคลุมหูเป็นจุดกลมสีเข้ม ข้างหัวมักมีรอยด่างสีคล้ำ แข็งและตีนแดงเข้ม ขณะบินปลายขอบปีกบินด้านหน้าขาวเด่นชัด มีสีดำเฉพาะขอบขนปลายปีก ขนชุดผสมพันธุ์ : หัวดำแกมน้ำตาลเข้ม ขาแดงเข้ม, นกวัยอ่อน : ขอบขนปีกบินและปลายหางดำ บนปีกมีแถบสีน้ำตาลเข้มแกมดำ ปากส้มปลายดำ แข้งและตีนส้ม', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '19'),
(83, 'นกพิราบป่า', 'Rock Pigeon', 'Columba livia', 'หัวสีเทาเข้ม หลังและอกเข้มกว่า ปีกเทาอ่อน แถบปีกดำ ปลายหางดำ ตัวผู้ : ใหญ่กว่าตัวเมีย คอเหลือบเขียวมากกว่า', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '20'),
(84, 'นกเขาใหญ่', 'Eastern Spotted Dove', 'Spilopelia chinensis', 'หัวเทา คอและลำตัวด้านล่างน้ำตาลแกมม่วง หลังคอมีแถบดำมีจุดขาวกระจาย หลังสีน้ำตาล หางยาวปลายขาว', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '20'),
(85, 'นกเขาไฟ', 'Red Collared Dove', 'Streptopelia tranquebarica', 'ตัวผู้ : หัวเทา ท้ายทอยด้านล่างมีแถบดำคาด ลำตัวสีน้ำตาลแดงแกมม่วง ปีกและหลังสีเข้มกว่าท้อง ขนปีกเทาดำ ตะโพกเทา ใต้หางขาว ขนหางคู่นอกๆขาวประมาณครึ่งหนึ่ง, ตัวเมีย : สีน้ำตาล', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '20'),
(86, 'นกเขาชวา', 'Zebra Dove', 'Geopelia striata', 'ขนาดเล็กกว่านกเขาใหญ่ ลำตัวสีน้ำตาลแกมเทา มีลายขวางสีดำที่คอ ลำตัว และปีก แข้งและตีนแดง ตัวผู้ : หน้าสีเทาแกมฟ้า ข้างคอเทา, ตัวเมีย : หน้าสีน้ำตาลมากกว่า มีลายตั้งแต่อกและท้อง', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '20'),
(87, 'นกเขาเปล้า', 'Thick-billed Green Pigeon', 'Treron curvirostra', 'ปลายปากหนาสีเหลืองแกมเขียว โคนปากแดง หัวเทา รอบตาสีฟ้าแกมเขียว ขนลำตัวเขียว ปีกมีแถบเหลือง หางคู่นอกๆ ปลายสีอ่อน ตัวผู้ : หลังสีน้ำตาลแดง, ตัวเมีย : หลังเขียว', 'เขตรักษาพันธุ์สัตว์ป่าห้วยขาแข้ง จังหวัดอุทัยธานี', '20'),
(88, 'นกคัคคูหงอน', 'Chestnut-winged Cuckoo', 'Clamator coromandus', 'หงอนแหลมยาว หัว หลัง และหางสีดำ รอบท้ายทอยขาว คอส้ม อกและท้องขาวแกมเทา ท้ายลำตัวเทาดำ ปีกน้ำตาลแดง', 'เขตรักษาพันธุ์สัตว์ป่าห้วยขาแข้ง จังหวัดอุทัยธานี', '21'),
(89, 'นกคัคคูพันธุ์หิมาลัย', 'Himalayan Cuckoo', 'Cuculus saturatus', 'ตัวผู้ : คล้ายนกคัดคูพันธุ์ยุโรปมาก แต่ท้องสีขาวแกมน้ำตาลอ่อน ลายที่ท้องกว้างและเข้มกว่าเล็กน้อย ส่วนใหญ่ลายที่ก้นไม่เด่นชัด, ตัวเมีย : เหมือนนกคัดคูพันธุ์ยุโรป แต่แถบอกสีน้ำตาลอ่อนมักกว้างกว่า, ตัวเมียชุดขนสีน้ำตาล : เหมือนนกคัคคูพันธุ์ยุโรป', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '21'),
(90, 'นกคัคคูพันธุ์อินเดีย', 'Indian Cuckoo', 'Cuculus micropterus', 'คล้ายนกคัดคูเหยี่ยวใหญ่ แต่หัวและหลังเทาอ่อนกว่า คางสีอ่อนกว่าหรือไม่เห็น ข้างคอและอกสีส้มแกมน้ำตาลกว้างกว่า ลายขวางที่ท้องไม่ชัดเจน หางสีเทาลายสีคล้ำแคบกว่า มีลายน้ำตาลอ่อนระหว่างสีเทากับดำ นกวัยอ่อน : ท้องเป็นลายเกล็ด', 'ศูนย์บริการนักท่องเที่ยวอุทยานแห่งชาติแม่วงก์ จังหวัดกำแพงเพชร', '21'),
(91, 'นกคัคคูเหยี่ยวใหญ่', 'Large Hawk Cuckoo', 'Hierococcyx sparverioides', 'หัวเทา หนังรอบตาเหลือง คางดำ ลำตัวด้านบนเทา อกน้ำตาลแดง มีขีดาสีคล้ำกระจาย อก ท้องและสีข้างมีลายขวางกว้างสีเทาดำ หางเทามีบั้งกว้างสีเข้ม นกวัยอ่อน : หัวน้ำตาลแกมเทา ท้ายทอยน้ำตาลแดง มีลายขีดที่อกและท้อง', 'จุดชมวิวขุนน้ำเย็นอุทยานแห่งชาติแม่วงก์ จังหวัดกำแพงเพชร', '21'),
(92, 'นกคัคคูเหยี่ยวอกแดง', 'Hodgson\'s Hawk Cuckoo', 'Hierococcyx nisicolor', 'คล้ายนกคัดคูเหยี่ยวมลายู แต่หัวและหลังเทากว่า คางดำ ท้องขาว อกและท้องตอนบนสีส้ม นกวัยอ่อน : หลังมีลายเกล็ดจากขอบขนสีน้ำตาลแดง อกและท้องมีลายสีน้ำตาลต่อกันเป็นแถบคล้ายนกคัดคูเหยี่ยวมลายู', 'อุทยานแห่งชาติรามคำแหง จังหวัดสุโขทัย', '21'),
(93, 'นกคัคคูแซงแซว', 'Square-tailed Drongo Cuckoo', 'Surniculus lugubris', 'ขนดำเป็นมัน หางเว้าตื้น ก้นและหางด้านล่างมีแถบขวางสีขาว ท้ายทอยมีแถบขาวเล็กๆ (เห็นได้ยาก) นกวัยอ่อน : แกมสีน้ำตาล มีจุดขาวกระจายทั่วไป', 'อุทยานแห่งชาติศรีสัชนาลัย จังหวัดสุโขทัย', '21'),
(94, 'นกอีวาบตั๊กแตน', 'Plaintive Cuckoo', 'Cacomantis merulinus', 'หนังรอบตาเทา หัว คอถึงอกตอนบนสีเทา หลัง ปีกเทาเข้ม หางดำ ขอบหางมีลายขาว อกตอนล่างถึงก้นสีน้ำตาลแดง ตัวเมียชุดขนสีน้ำตาล : คล้ายนกคัดคูลาย แต่คิ้วบางกว่า คอและอกสีน้ำตาลแดง', 'คลองหนองหลวง จังหวัดพิษณุโลก', '21'),
(95, 'นกคัคคูมรกต', 'Asian Emerald Cuckoo', 'Chrysococcyx maculatus', 'ปากเหลืองปลายดำ หางคู่นอกมีขอบขาวดำ ตัวผู้ : หัว อก หลัง ปีก และหางสีเขียวมรกตเหลือบเป็นมัน อกตอนล่างถึงก้นขาว มีลายขวางสีเขียว, ตัวเมีย : หัวน้ำตาลแดง คอและอกแซมสีน้ำตาลแดง ลำตัวด้านบนสีเขียวแกมเหลืองวาวเป็นมัน ขอบหางคู่นอกๆสีน้ำตาลแดง', 'เขตรักษาพันธุ์สัตว์ป่าภูหลวง จังหวัดเลย', '21'),
(96, 'นกคัคคูสีม่วง', 'Violet Cuckoo', 'Chrysococcyx xanthorhynchus', 'ปากและหนังรอบตาสีส้ม ตัวผู้ : ลำตัวด้านบน คอ และอกสีม่วงเหลือบเป็นมัน ท้องขาวมีลายขวางสีดำ, ตัวเมีย : คล้ายนกคัดคูสีทองแดง แต่หน้าผากและหลังเรียบสีน้ำตาลแกมเหลือง ใบหน้าและลำตัวด้านล่างมีลายขวาง', 'อุทยานแห่งชาติตาดหมอก จังหวัดเพชรบูรณ์', '21'),
(97, 'นกบั้งรอกใหญ่', 'Green-billed Malkoha', 'Phaenicophaeus tristis', 'ปากเขียวขุ่น หนังรอบตาแดงมีขนสีขาวโดยรอบ หัวสีเทาอ่อน ปีกและหลังสีน้ำตาลเหลือบเขียว ลำตัวด้านล่างเทาแซมขีดดำเล็กๆ หางยาวมาก ปลายหางขาว', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '21'),
(98, 'นกกาเหว่า', 'Asian Koel', 'Eudynamys scolopaceus', 'ปากหนาสีออกเขียว หางยาว ตัวผู้ : ขนลำตัวดำเหลือบเขียว ตาแดงเข้ม, ตัวเมีย : ขนลำตัวน้ำตาลถึงน้ำตาลดำมีลายจุดและลายขวางสีขาวและน้ำตาลอ่อน', 'คลองหนองหลวง จังหวัดพิษณุโลก', '21'),
(99, 'นกกระปูดใหญ่', 'Greater Coucal', 'Centropus sinensis', 'ขนาดใหญ่กว่านกกะปูดชนิดอื่น หัว คอ ลำตัวด้านล่าง และหางดำเหลือบม่วง หลังและปีกสีน้ำตาลแดง นกวัยอ่อน : หลังและปีกมีลายขวางสีเข้ม หัวและลำตัวมีจุดขาว', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '21'),
(100, 'นกกระปูดเล็ก', 'Lesser Coucal', 'Centropus bengalensis', 'หัว คอ และหลังสีน้ำตาลแกมเทา มีลายขีดสีน้ำตาลอ่อนกระจาย ปีกน้ำตาลแดง หางน้ำตาลมีลายขวางปลายสีดำ ขนชุดผสมพันธุ์ : คล้ายนกกะปูดใหญ่ หัว คอ อก ท้องและหางดำ มีลายขีดสีขาวที่หัวและหลัง', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '21'),
(101, 'นกแสก', 'Eurasian Barn Owl', 'Tyto javanica', 'วงหน้ารูปหัวใจสีขาวขบน้ำตาลแดง ตาน้ำตาลแดงคล้ำ ลำตัวด้านบนน้ำตาลแกมเหลืองแซมด้วยสีเทา มีจุดขาวและน้ำตาลแดงเข้มกระจาย ลำตัวด้านล่างขาวมีจุดสีเทาและน้ำตาลแดงกระจาย นกบางตัวหน้าอกสีน้ำตาลแดง', 'อำเภอหล่มสัก จังหวัดเพชรบูรณ์', '22'),
(102, 'นกเค้าเหยี่ยวพันธุ์เหนือ', 'Northern Boobook', 'Ninox japonica', 'คล้ายนกเค้าเหยี่ยวมาก แต่ลายจุดที่อกและท้องเป็นรูปหยดน้ำ สีลำตัวด้านบนเข้มกว่าเล็กน้อย ขณะเกาะปลายปีกยาวกว่าเล็กน้อย', 'เป็นนกอพยพเข้ามาประเทศไทยในฤดูหนาว หรือนอกฤดูผสมพันธุ์', '23'),
(103, 'นกฮูก, นกเค้ากู่', 'Collared Scops Owl', 'Otus lettia', 'ตาน้ำตาลแดงเข้ม ขนลำตัวน้ำตาล รอบคอสีน้ำตาลแกมเหลือง ในธรรมชาติมักสังเกตเห็นได้ยาก หน้าผากและคิ้วน้ำตาลอ่อน ลำตัวด้านล่างมีลายขีดดำเป็นเส้นบาง ๆ', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '23'),
(104, 'นกเค้าหูยาวเล็ก', 'Oriental Scops Owl', 'Otus sunia', 'ขนาดเล็ก ตาเหลือง ขนลำตัวสีน้ำตาลแดงหรือเทา ต่างจากนกเค้าขนาดเล็กชนิดอื่นที่ลำตัวด้านล่างมีลายขีดสีดำใหญ่และหนา', 'เป็นนกประจำถิ่นพบในป่าดิบและป่าโปร่ง เช่น ป่าเต็งรัง ในภาคเหนือ ภาคตะวันตกและภาคตะวันออกเฉียงเหนือ', '23'),
(105, 'นกเค้าโมง, นกเค้าแมว', 'Asian Barred Owlet', 'Glaucidium cuculoides', 'ตาเหลือง คล้ายนกเค้าแคระ แต่ขนาดใหญ่กว่า หัวและลำตัวด้านบนมีลายขวางสีน้ำตาลอ่อนชัดเจนกว่า ไม่มีลายคล้ายตาปลอมที่ท้ายทอย อกและสีข้างมีลายน้ำตาลแดงคล้ำ ท้องขาว', 'เขตรักษาพันธุ์สัตว์ป่าห้วยขาแข้ง จังหวัดอุทัยธานี', '23'),
(106, 'นกเค้าจุด', 'Spotted Owlet', 'Athene brama', 'ตาเหลือง คิ้วกว้างและวงหน้าขาว หน้าเทาคล้ำ หัวและลำตัวด้านบนเทาคล้ำ มีจุดขาวกระจาย อกและสีข้างมีลายเทาเข้มสลับขาว ท้องขาว', 'คลองหนองหลวง จังหวัดพิษณุโลก', '23'),
(107, 'นกเค้าแคระ', 'Collared Owlet', 'Glaucidium brodiei', 'นกเค้าขนาดเล็กมาก ตาเหลือง หน้าสีคล้ำกว่านกเค้าโมง มีคิ้วบางๆ สีขาวเหนือตา หัวน้ำตาลเข้มมีลายจุดเล็กสีน้ำตาลเหลือง ท้ายทอยมีลวดลายคล้ายตาปลอมเพื่อหลอกศัตรู ลำตัวด้านบนน้ำตาลมีลายน้ำตาลแดงคล้ำกระจาย อกและสีข้างมีลายแถบใหญ่สีน้ำตาลแดงคล้ำ ท้องขาว', 'ศูนย์บริการนักท่องเที่ยวอุทยานแห่งชาติแม่วงก์ จังหวัดกำแพงเพชร', '23'),
(108, 'นกแอ่นพันธุ์หิมาลัย', 'Himalayan Swiftlet', 'Aerodramus brevirostris', 'ลำตัวด้านบนดำแกมน้ำตาล ตะโพกมีแถบสีเทา ลำตัวด้านล่างเทาแกมน้ำตาล คอและข้างหัวสีเข้มกว่านกแอ่นกินรัง หางเว้าลึกกว่า', 'คลองหนองเหล็ก จังหวัดพิษณุโลก', '24'),
(109, 'นกแอ่นกินรัง', 'Pale-rumped  Swiftlet', 'Aerodramus germani', 'แถบตะโพกแกมเทาหรือสีจางกว่านกแอ่นชนิดอื่นในสกุลเดียวกัน ลำตัวด้านบนดำ ด้านล่างสีน้ำตาลแกมเทา คอและอกตอนบนสีจางกว่าท้องและคาง หางเว้า', 'คลองหนองหลวง จังหวัดพิษณุโลก', '24'),
(110, 'นกแอ่นตาล', 'Asian Palm Swift', 'Cypsiurus balasiensis', 'ลำตัวผอมบาง ปีกแหลม เรียวแคบ ลำตัวสีน้ำตาลเข้ม หางแหลม เมื่อกางออกเป็นแฉกลึก', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '24'),
(111, 'นกแอ่นบ้าน', 'House Swift', 'Apus nipalensis', 'ปลายปีกมน ลำตัวดำเหลือบน้ำเงิน คอและตะโพกขาวชัดเจน ปลายหางตัดหรือเว้าเล็กน้อย', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '24'),
(112, 'นกตะขาบทุ่ง', 'Indochinese Roller', 'Coracias affinis', 'ขณะเกาะสีค่อนข้างคล้ำ ปากดำ ลำตัวด้านบนสีน้ำตาลแกมเขียวคล้ำ ลำตัวด้านล่างสีม่วงแกมน้ำตาลมีลายขีดเล็กๆ สีฟ้าที่คอและอกตอนบน ขณะปีกและหางสีฟ้าสดสลับน้ำเงินเข้ม', 'เขตรักษาพันธุ์สัตว์ป่าห้วยขาแข้ง จังหวัดอุทัยธานี', '25'),
(113, 'นกตะขาบดง', 'Oriental Dollarbird', 'Eurystomus orientalis', 'ปากกว้าง สีแดงสด หัวและลำตัวสีน้ำเงินแกมเขียวเข้ม ขณะบินขนปลายปีกน้ำเงินเข้มมีแถบค่อนข้างกลมสีขาวทั้งสองข้าง นกวัยอ่อน : ตัวสีน้ำตาล ปากดำ', 'เขาพนมกาวอำเภอตะพานหิน จังหวัดพิจิตร', '25'),
(114, 'นกกะเต็นน้อยธรรมดา', 'Common Kingfisher', 'Alcedo atthis', 'ลำตัวด้านบนสีน้ำเงินเหลือบเขียว หลังและตะโพกฟ้าวาว ขนคลุมหูน้ำตาลแดงต่อกับแถบขาว คอขาว ลำตัวด้านล่างสีส้มแกมน้ำตาล แข้งและตีนแดง ตัวเมีย : ปากล่างสีน้ำตาลแดง', 'ตำบลปากพระ อำเภอเมืองสุโขทัย', '26'),
(115, 'นกกะเต็นน้อยสามนิ้ว', 'Oriental Dwarf Kingfisher', 'Ceyx erithaca', 'ปากแดงสด หัว ตะโพก และหางสีชมพูแกมม่วง หน้าเหลืองส้ม ขนคลุมหู มีแถบน้ำเงินเข้ม และขาว หลังน้ำเงินเข้ม ลำตัวด้านล่างเหลืองแกมส้ม แข้ง และตีนแดง', 'อุทยานแห่งชาติภูสวนทราย จังหวัดเลย', '26'),
(116, 'นกกะเต็นแดง', 'Ruddy Kingfisher', 'Halcyon coromanda', 'ลำตัวด้านบนสีชมพูแกมม่วง ท้องน้ำตาลแดง ปากบนแดงสด ปากล่างอาจแกมสีน้ำตาลเหลือง ตะโพกขาวแซมสีฟ้าวาว', 'จุดชมวิวขุนน้ำเย็นอุทยานแห่งชาติแม่วงก์ จังหวัดกำแพงเพชร', '26'),
(117, 'นกกะเต็นปักหลัก', 'Pied Kingfisher', 'Ceryle rudis', 'ปากแหลมยาวสีดำ หัวมีหงอนสั้นๆ แถบตาดำ คิ้วขาว ขนสลับลายขาวดำ เวลาบินปีกมีแถบขาวขนาดใหญ่ ตัวผู้ : แถบอก 2 แถบ แถบบนกว้างกว่าแถบล่าง, ตัวเมีย : แถบอก 1 แถบ มักไม่ต่อเนื่อง', 'คลองหนองหลวง จังหวัดพิษณุโลก', '26'),
(118, 'นกกะเต็นอกขาว', 'White-throated Kingfisher', 'Halcyon smyrnensis', 'หัว หลังตอนบน ไหล่ และท้องสีน้ำตาลเข้ม คอและอกขาว ปากแดงสด ปีก หลัง และหางฟ้าเข้ม หลังตอนล่างและตะโพกฟ้าวาว แข้งและตีนแดง ขณะบินมีแถบขาวใหญ่ที่ปีก', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '26'),
(119, 'นกกะเต็นหัวดำ', 'Black-capped Kingfisher', 'Halcyon pileata', 'หัวดำ ปากแดงสด รอบคอและอกขาว ไล่ระดับเป็นสีเนื้อแกมน้ำตาล ไหล่ดำ ปีก หลัง และหางสีน้ำเงินสด ขณะบินมีแถบขาวใหญ่ที่ปีก', 'อำเภอตะพานหิน จังหวัดพิจิตร', '26'),
(120, 'นกกินเปี้ยว', 'Collared Kingfisher', 'Todiramphus chloris', 'หัวและลำตัวด้านบนเขียวแกมฟ้า รอบคอและลำตัวด้านล่างขาว ปีกสีฟ้าไม่มีลาย ปากบนดำ ปากล่างสีเนื้อ', 'ตำบลแหลมผักเบี้ย จังหวัดเพชรบุรี', '26'),
(121, 'นกกะเต็นใหญ่ธรรมดา', 'Stork-billed Kingfisher', 'Pelargopsis capensis', 'รูปร่างและขนาดคล้ายนกกระเต็นใหญ่ ปีกสีน้ำตาล แต่หัวเทา อกตอนบนมีลายเกล็ดสีน้ำตาลจางๆ หลัง ปีก และหางสีน้ำเงินคล้ำ ตะโพกฟ้าสด', 'อำเภอตะพานหิน จังหวัดพิจิตร', '26'),
(122, 'นกจาบคาเล็ก', 'Green Bee-eater', 'Merops orientalis', 'ลำตัวเขียวสด หัวและท้ายทอยสีส้มแกมน้ำตาล แถบตาดำ ใต้แถบตาสีฟ้า อกมีแถบดำโค้งครึ่งวงกลม หางคู่กลางแหลมยาว', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '27'),
(123, 'นกจาบคาหัวเขียว', 'Blue-tailed Bee-eater', 'Merops philippinus', 'หัวเขียวแกมน้ำตาลส้ม แถบตาดำ ใต้แถบตาและคางขาวแกมเหลือง คอและอกตอนบนสีส้มแกมน้ำตาล ตะโพกฟ้าวาว หางสีฟ้า หางคู่กลางยาว', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '27'),
(124, 'นกจาบคาหัวสีส้ม', 'Chestnut-headed Bee-eater', 'Merops leschenaulti ', 'หัวถึงหลังสีส้ม แถบตาดำ คอและอกเหลืองสด อกมีแถบเล็กๆ สีดำและส้ม ลำตัวเขียวตะโพกสีฟ้าวาว ไม่มีห่างคู่กลางแหลมยาว', 'ศูนย์บริการนักท่องเที่ยวอุทยานแห่งชาติแม่วงก์ จังหวัดกำแพงเพชร', '27'),
(125, 'นกกะรางหัวขวาน', 'Eurasian Hoopoe', 'Upupa epops', 'ปากยาวโค้ง ลำตัวสีเหลืองแกมน้ำตาล หัวมีหงอนยาว ปลายสีดำกางออกในแนวตั้งคล้ายหมวกอินเดียนแดง ปีก หลัง และหางสีดำสลับลายสีขาว ท้องด้านล่างและขนคลุมใต้หางสีขาว', 'วัดพระพุทธบาทเขาทราย จังหวัดพิจิตร', '28'),
(126, 'นกตีทอง', 'Coppersmith Barbet', 'Psilopogon haemacephalus', 'หน้าผากแดง ใบหน้าสีดำ มีแถบเหลืองด้านบนและด้านล่างของตา คอสีเหลือง คอด้านล่างมีแถบแดง อกตอนบนสีเหลือง อกตอนล่างและท้องสีขาวแกมเหลืองมีลายขีดสีเขียว', 'มอนเทอร์เรย์พาร์ค รีสอร์ท ตำบลเขาทราย จังหวัดพิจิตร', '29'),
(127, 'นกคอพัน', 'Eurasian Wryneck', 'Jynx torquilla', 'ลำตัวเพรียว ปากเรียวแหลมตรง แถบตาดำ ลำตัวด้านบนสีเทามีลายขีดเล็ก ๆ ปีกสีน้ำตาลมีลายจุดและลายขวางสีเข้มและอ่อนกระจาย ลำตัวด้านล่างขาวมีลายขวางสีน้ำตาลเข้ม', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '30'),
(128, 'นกหัวขวานด่างอกลายจุด', 'Freckle-breasted Woodpecker', 'Dendrocopos analis', 'หน้าสีขาว แถบมุมปากแยก 2 แฉกสั้นๆ ท้ายทอยสีดำ หลังดำมีจุดสีขาวเรียงกันเป็นแถบขวาง หางดำแถบขาว อกสีน้ำตาลแกมสีเนื้อมีจุดดำเล็กๆ กระจาย ก้นสีมพูแกมแดง ตัวผู้ : หน้าผากแดง, ตัวเมีย : หน้าผากดำ', 'หนองจรเข้ จังหวัดสุโขทัย', '30'),
(129, 'นกแต้วแล้วธรรมดา', 'Blue-winged Pitta', 'Pitta moluccensis', 'ตัวผู้และตัวเมียเหมือนกัน หัวดำ คิ้วกว้างสีน้ำตาลแกมส้ม คอขาว ลำตัวด้านบนเขียว หัวไหล่และตะโพกฟ้าสด ขนปีกบินดำมีแถบขาวเป็นวงใหญ่เห็นชัดขณะบิน ลำตัวด้านล่างน้ำตาลเหลือง กลางท้องถึงก้นแดง', 'อุทยานแห่งชาติรามคำแหง จังหวัดสุโขทัย', '31'),
(130, 'นกแต้วแล้วอกเขียว', 'Hooded Pitta', 'Pitta sordida', 'ตัวผู้และตัวเมียเหมือนกันมาก มีหน้าผาก กระหม่อม ท้ายทอยเป็นสีน้ำตาลเข้ม หน้าจนถึงคอเป็นสีดำ ปากหนาสีดำ ลำตัวเป็นสีเขียวกลมกลืนกับสภาพป่า ขนคลุมท้องดำต่อด้วยสีแดงถึงก้น ขนคลุมโคนหางด้านบนและตะโพกสีฟ้าสดใสเป็นมัน ขนหางสั้น ขาและนิ้วเท้ายาว นกชนิดนี้เป็นนกที่หวงอาณาเขตมาก ไม่ว่าจะเป็นช่วงฤดูผสมพันธุ์หรือไม่ นอกฤดูผสมพันธุ์', 'สามารถพบได้ในภาคกลาง ภาคตะวันตก ภาคตะวันออก และ ภาคใต้', '31'),
(131, 'นกแต้วแล้วพันธุ์จีน', 'Fairy Pitta', 'Pitta nympha', 'ส่วนหัวมีสีขาว ส้ม และดำ ปากมีสีดำ ลำตัวมีสีขาวแต้มแดง ปีกมีสีเขียวแต้มฟ้าขามีสีแดงชมพู', 'เป็นนกอพยพผ่านช่วงฤดูใบไม้ผลิ และฤดูใบไม้ร่วงมีแนวโน้มจะสูญพันธุ์', '31'),
(132, 'นกแอ่นพง', 'Ashy Woodswallow', 'Artamus fuscus', 'ปากหนาสีเทาแกมฟ้า หน้าดำหรือเทาเข้ม หัวและลำตัวด้านบนเทา อกและลำตัวด้านล่างน้ำตาลแกมเหลือง ก้นขาว ขณะบินปีกเป็นรูปสามเหลี่ยม ใต้ปีกขาวแกมน้ำตาลอ่อน หางสั้น นกวัยอ่อน : ขนลำตัวแกมน้ำตาล มีลายเกล็ดบนหลังและปีก ปากน้ำตาล', 'คลองหนองเหล็ก จังหวัดพิษณุโลก', '32'),
(133, 'นกขมิ้นน้อยธรรมดา', 'Common Iora', 'Aegithina tiphia', 'ปากสีเทา ตัวผู้ : หัวและลำัตัวด้านบนเขียวแกมเหลือง ลำตัวด้านล่างเหลืองสดกว่า หางดำ ปีกมีแถบขาว 2 แถบ ชริดย่อย horizoptera ช่วงผสมพันธุ์หัวและหลังดำชัด, ตัวเมีย : รอบตาเหลือง สีซีดกว่า ปีกคล้ำ แถบขาวไม่เด่นชัดเท่าตัวผู้', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '33'),
(134, 'นกเฉี่ยวบุ้งใหญ่', 'Black-winged Cuckooshrike', 'Coracina melaschistos', 'ตัวผู้ : ปีกดำกับขนลำตัวสีเทา ตะโพกสีเทาอ่อนกว่า ท้องตอนล่างและก้นขาว หางด้านบนสีดำ ด้านล่างเป็นชั้นสีดำปลายขาว, ตัวเมีย : วงรอบตาขาวไม่ต่อเนื่อง หน้าและลำตัวด้านล่างสีเทาเข้มกว่าชนิดอื่นๆ มีลายขวางสีดำ', 'อุทยานแห่งชาติน้ำหนาว จังหวัดเพชรบูรณ์', '34'),
(135, 'นกพญาไฟสีกุหลาบ', 'Rosy Minivet', 'Pericrocotus roseus', 'ตัวผู้ : หัว ท้ายทอย และหลังเทา ปีกดำมีแถบสีแดง คอขาว ลำตัวด้านล่างและตะโพกชมพูแกมแดง หางดำขอบหางคู่นอกๆแดง ชนิดย่อย stanfordi คล้ายนกพญาไฟตะโพกสีน้ำตาล แต่หน้าผากและคิ้วสีชมพู ลำตัวด้านล่างน้ำตาลแกมเหลือง แถบปีกและขอบหางชมพู ตะโพกชมพูแดง, ตัวเมีย : ส่วนสีชมพูและชมพูแกมแดงในตัวผู้เปลี่ยนเป็นสีเหลือง', 'ศูนย์บริการนักท่องเที่ยวอุทยานแห่งชาติแม่วงก์ จังหวัดกำแพงเพชร', '34');
INSERT INTO `birds` (`bird_id`, `bird_name`, `bird_commonname`, `bird_sciname`, `bird_description`, `bird_habitat`, `bird_family_id`) VALUES
(136, 'นกพญาไฟตะโพกสีน้ำตาล', 'Brown-rumped Minivet', 'Pericrocotus cantonensis', 'จำแนกจากนกพญาไฟสีเทาที่ตะโพกกับหลังสีต่างกัน ตัวผู้ : หัวตาดำ หน้าผากขาว กระหม่อม ท้ายทอย และแถบหลังตาสีเข้ม ลำตัวด้านบนเทาแตกต่างจากตะโพกสีน้ำตาลแกมเหลือง ปีกดำไม่มีแถบสี ลำตัวด้านล่างขาว อกและท้องแกมน้ำตาลเหลืองจางๆ หางดำมีขอบขาว, ตัวเมีย : หน้าผากและคิ้วแคบๆ สีขาวแกมน้ำตาลอ่อน กระหม่อม ท้ายทอย และแถบตาน้ำตาลแกมเทาจางๆ อาจเห็นแถบปีกน้ำตาลเหลือง', 'อุทยานแห่งชาติตาดหมอก จังหวัดเพชรบูรณ์', '34'),
(137, 'นกพญาไฟสีเทา', 'Ashy Minivet', 'Pericrocotus divaricatus', 'ตัวผู้ : หน้าผากขาวตัดกับหัวตา กระหม่อม ท้ายทอย และแถบตาดำ หลังเทา ตะโพกสีเดียวกับหลัง ลำตัวด้านบนเทา ลำตัวด้านล่างขาวสะอาดกว่านกพญาไฟตะโพกสีน้ำตาลหางดำขอบขาว, ตัวเมีย : ต่างจากนกพญาไฟตะโพกสีน้ำตาลที่หน้าผากตอนหน้าและหัวตาขาว ลำตัวด้านบนและตะโพกเทา', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '34'),
(138, 'นกอีเสือลายเสือ', 'Tiger Shrike', 'Lanius tigrinus', 'ตัวผู้ขนชุดผสมพันธุ์ : หัวและหลังตอนบนเทา แถบตาดำ ข้างคอ คอ และลำตัวด้านล่างขาว หลัง ปีก และหางน้ำตาล ลำตัวด้านบนและปีกมีลายเกล็ดสีดำ ไม่มีจุดขาวที่ปีก, ตัวเมียขนชุดผสมพันธุ์ : คล้ายตัวผู้ แต่หัวตาเทา แถบตาจางกว่า มีขอบขาวคล้ายคิ้วด้านบนแถบตา ลำตัวด้านล่างน้ำตาลเหลืองมีลายเกล็ดสีดำกระจาย, ขนนอกฤดูผสมพันธุ์ : คล้ายนกวัยอ่อน แต่มีแถบตาสีคล้ำ, นกวัยอ่อน : หัวสีน้ำตาล ลำตัวด้านล่างมีลายเกล็ดชัดเจนกว่า', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '35'),
(139, 'นกอีเสือสีน้ำตาล', 'Brown Shrike', 'Lanius cristatus', 'ตัวผู้ : หัวและลำตัวบนสีน้ำตาล แถบตาดำมีขอบขาวด้านบน ข้างคอ คอ และลำตัวด้านล่างขาว ข้างลำตัวยน้ำตาลอ่อน, ตัวเมีย : สีซีดกว่าตัวผู้ มีลายเกล็ดจางๆที่อกและสีข้าง, นกวัยอ่อน : สีคล้ำกว่า มีลายเกล็ดที่หัว หลัง และลำตัวด้านล่าง ชนิดย่อย lucionensis กระหม่อมสีเทาตลอดไปถึงหลัง ชนิดย่อย superciliosus (พบไม่บ่อย) กระหม่อมสีน้ำตาลแดงสดใส หน้าผากและลำตัวด้านล่างขาวมาก', 'จังหวัดอุตรดิตถ์', '35'),
(140, 'นกอีเสือหลังแดง', 'Burmese Shrike', 'Lanius collurioides', 'หัวเทาเข้ม หน้าผากและแถบตาดำ ข้างคอ คอ และลำตัวด้านล่างขาว ลำตัวด้านบนน้ำตาลแดง ปีกมีจุดขาวเห็นได้ชัด ตัวเมีย : สีจางกว่าตัวผู้ หัวตาขาว, นกวัยอ่อน : คล้ายตัวเมีย แต่มีแต่มีลายเกล็ดทั้งด้านบนและล่าง จุดขาวที่ปีกเล็กมากหรืออาจมองไม่เห็น', 'หมู่บ้านวังเพชร จังหวัดพิษณุโลก', '35'),
(141, 'นกอีเสือหลังเทา', 'Grey-backed Shrike', 'Lanius tephronotus', 'หัว ท้ายทอย และหลังเทา ตะโพกน้ำตาลแดง ปีกดำไม่มีแถบขาว คอ ข้างคอ และอกตอนบนขาว ข้างอก ลำตัวด้านล่าง และก้นน้ำตาลแดงแกมเหลือง หางดำ นกวัยอ่อน : สีซีดกว่า ลำตัวด้านบนและด้านล่างมีลายเกล็ด', 'ศูนย์บริการนักท่องเที่ยวอุทยานแห่งชาติแม่วงก์ จังหวัดกำแพงเพชร', '35'),
(142, 'นกอีเสือหัวดำ', 'Long-tailed Shrike', 'Lanius schach', 'หัวและท้ายทอยดำ ลำตัวด้านบนน้ำตาลแดง ปีกดำมีแถบขาว เห็นชัดเจนขณะบิน หางยาวสีดำ ข้างคอ คอ และลำตัวด้านล่างขาว ตอนท้ายลำตัวและก้นน้ำตาลแดง นกวัยอ่อน : หัวและท้ายทอยน้ำตาลอ่อน มีลายเกล็ดทั้งด้านบนและด้านล่างลำตัว', 'คลองหนองหลวง จังหวัดพิษณุโลก', '35'),
(143, 'นกขมิ้นท้ายทอยดำ', 'Black-naped Oriole', 'Oriolus chinensis', 'ตัวผู้ : ขนลำตัวเหลืองสด ปากสีชมพูแกมส้ม ตาแดง แถบตากว้างสีดำขยายใหญ่และเชื่อมกันที่ท้ายทอย ปีกและหางสีดำและเหลือง, ตัวเมีย : หลังและปีกเหลืองแกมเขียวคล้ำ, นกไม่เต็มวัย : คล้ายนกตัวเมีย แต่ขนเขียวคล้ำกว่า แถบตาไม่ชัดเจน มีลายขีดดำที่อกและท้อง, นกวัยอ่อน : ท้องขาวหรือเหลืองแกมขาวมีขีดดำ ปากดำ', 'อุทยานแห่งชาติน้ำหนาว จังหวัดเพชรบูรณ์', '36'),
(144, 'นกแซงแซวหางปลา', 'Black Drongo', 'Dicrurus macrocercus', 'ขนลำตัวดำเหลือบน้ำเงินเล็กน้อย หางแฉกลึก บางครั้งมีจุดขาวที่หัวตา นกวัยอ่อน : อกเทาเข้ม ที่ท้องและตะโพกมีสีขาวแซม', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '37'),
(145, 'นกแซงแซวสีเทา', 'Ashy Drongo', 'Dicrurus leucophaeus', 'รูปร่างคล้ายนกแซงแซวหางปลามาก แต่หางแฉกไม่ลึกเท่า สีเทาเข้มเกือบดำ หน้าสีเข้มกว่า ชนิดย่อย leucogenis สีเทาอ่อน หน้าผากดำ หน้าและก้นขาว ชนิดย่อยอพยพ salangensis สีเทาเข้มกว่า leucogenis แต่หน้าขาวน้อยกว่า', 'อำเภอกงไกรลาศ จังหวัดสุโขทัย', '37'),
(146, 'นกแซงแซวปากกา', 'Crow-billed Drongo', 'Dicrurus annectans', 'ลำตัวหนากว่านกแซงแซวหางปลา ปากหนาและกว้างกว่า หางแฉกตื้นกว่าแต่แผ่กว้างกว่า', 'เขตรักษาพันธุ์สัตว์ป่าห้วยขาแข้ง จังหวัดอุทัยธานี', '37'),
(147, 'นกแซงแซวหงอนขน', 'Hair-crested Drongo', 'Dicrurus hottentottus', 'ปากยาวโค้งกว่านกแซงแซวชนิดอื่น หางตัด ขอบหางคู่นอกแผ่กว้างและงอนขึ้นด้านบน หน้าผากมีขนยาวคล้ายเส้นผม ขนลำตัวดำเป็นมัน ปีกและหางเหลือบเขียว', 'ตำบลเมืองเก่า จังหวัดพิจิตร', '37'),
(148, 'นกแซงแซวหางบ่วงใหญ่', 'Greater Racket-tailed Drongo', 'Dicrurus paradiseus', 'หางคู่นอกมีก้านขนยื่นยาวเหมือนนกแซงแซวหางบ่วงเล็ก แต่ปลายแผ่ออกด้านเดียวและบิด ก้านขนอาจยาวได้ถึง 30 ซม. ขนาดตัวใหญ่กว่า หน้าผากมีหงอนตั้งเป็นกระจุก ขนลำตัวดำเหลือบน้ำเงินเล็กน้อย หางแฉกตื้น ๆ นกวัยอ่อน : หงอนสั้น ก้านขนหางคู่นอกไม่ยาว', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '37'),
(149, 'นกแซงแซวหางบ่วงเล็ก', 'Lesser Racket-tailed Drongo', 'Dicrurus remifer', 'หน้าผากมีหงอนสั้นๆ คลุมโคนปาก ทำให้ดูปากสั้นและหนา ขนลำตัวดำเหลือบน้ำเงินเป็นมัน ปีกและหางเหลือบเขียว ปลายหางตัด ก้านขนหางคู่นอกยาวมาก อาจยาวออกมาถึง 40 ซม. ปลายแผ่ออกสองข้างเป็นแผ่นรูปวงรียาว', 'จุดชมวิวขุนน้ำเย็นอุทยานแห่งชาติแม่วงก์ จังหวัดกำแพงเพชร', '37'),
(150, 'นกอีแพรดแถบอกดำ', 'Pied Fantail', 'Rhipidura javanica', 'ลำตัวด้าบนบนดำ หลังแกมน้ำตาล คิ้วบางและสั้นสีขาว คอและลำัตัวด้านล่างขาว มีแถบกว้างสีดำพาดอก หางสีดำปลายขาว', 'คลองหนองหลวง จังหวัดพิษณุโลก', '38'),
(151, 'นกแซวสวรรค์', 'Blyth\'s Paradise-flycatcher', 'Terpsiphone atrocaudata', 'ตัวผู้ : หนังรอบตาและปากฟัาเข้ม ขนลำตัวและหางดำเข้ม ขนหางคู่กลางอาจยาวมากกว่า 23 ซม. หลังตอนบนถึงตะโพกเหลือบม่วงเล็กน้อย อกตอนล่าง ท้อง และก้นขาว, ตัวเมีย : คล้ายนกแซวสวรรค์ตัวเมียหรือตัวผู้หางสั้น แต่กระหม่อมสีทึมกว่า กลืนกับสีหัว อกสีเทาตัดกับท้องขาว', 'สวนหลวง ร.9 และ อำเภอตะพานหิน จังหวัดพิจิตร', '39'),
(152, 'นกจับแมลงจุกดำ', 'Black-naped Monarch', 'Hypothymis azurea', 'ตัวผู้ : ปากกว้างสีฟ้าเข้ม ท้ายทอยมีีหงอนดำสั้น หัว อก ลำตัวด้านบน และหางฟ้าเข้ม อกตอนล่างถึงก้นขาว หงอน หัวตา และแถบคาดอก, ตัวเมีย : สีหม่นกว่าตัวผู้ ลำตัวด้านบนเทาแกมน้ำตาล อกเทาไม่มีแถบดำคาดอก', 'อุทยานแห่งชาติศรีสัชนาลัย จังหวัดสุโขทัย', '39'),
(153, 'นกแซวสวรรค์หัวดำ', 'Amur Paradise-flycatcher', 'Terpsiphone incei', 'ตัวผู้ชุดขนสีน้ำตาลแดง : หัวดำเข้มตัดกับอกสีเทาชัดเจน หางยาวมากอาจเกิน 27 เซนติเมตร ลำตัวด้านบน ปีก หางสีน้ำตาลเข้ม, ตัวผู้ชุดขนสีขาว : หัวดำ ลำตัวสีขาว แยกได้ยากในธรรมชาติกับนกแซวสวรรค์ (Blyth\'s Paradise-flycatcher) ชุดขนสีขาว, ตัวเมีย : หางสั้น หัวดำ ขนลำตัวสีน้ำตาลแดงเข้ม', 'อำเภอตะพานหิน จังหวัดพิจิตร', '39'),
(154, 'นกขุนแผน', 'Red-billed Blue Magpie', 'Urocissa erythrorhyncha', 'ปากแดงสด หัวถึงอกดำ ท้ายทอยถึงหลังตอนบนเป็นแถบขาว ลำตัวด้านบนและปีกฟ้า ลำตัวด้านล่างขาว แข้งและตีนแดง หางฟ้า ปลายขนหางและปีกขลิบดำและขาว นกวัยอ่อน : สีซีดกว่า ปากดำ หางสั้น', 'อุทยานแห่งชาติน้ำหนาว จังหวัดเพชรบูรณ์', '40'),
(155, 'อีกา', 'Eastern Jungle Crow', 'Corvus levaillantii', 'ขนลำตัวดำสนิท ปากใหญ่ ยาว หนาเป็นสัน หน้าผากตั้งเป็นมุมฉากกับปาก', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '40'),
(156, 'นกจับแมลงหัวเทา', 'Grey-headed Canary-flycatcher', 'Culicicapa ceylonensis', 'เป็นนกขนาดเล็กมาก หัวมีหงอนสั้นตั้งเป็นสัน หัวและอกเทา ตัดกับลำตัวด้านบนและหางเขียวแกมเหลือง ตะโพกและลำตัวด้านล่างเหลือง', 'ตำบลปากพระ อำเภอเมืองสุโขทัย', '41'),
(157, 'นกจาบฝนปีกแดง', 'Indochinese Bushlark', 'Mirafra erythrocephala', 'ลำตัวอวบหนากว่านกจาบฝนชนิดอื่น คล้ายนกจาบฝนเสียงใสมาก แต่ขณะบินสีน้ำตาลแดงที่ปีกมากกว่า หางสั้นและไม่มีสีขาวที่ขอบหาง หน้าและขนคลุมหูสีน้ำตาลมีขอบสีเข้ม ลายที่อกเป็นจุดใหญ่ค่อนข้างหนาสีดำเรียงกันเป็นแถวตามยาว', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '42'),
(158, 'นกจาบฝนเสียงสวรรค์', 'Oriental Skylark', 'Alauda gulgula', 'ต่างจากนกจาบฝนชนิดอื่นที่ปากเรียวยาวกว่า ท้ายทอยมีหงอนสั้น ๆ บางครั้งอาจเห็นไม่ชัดเจนเนื่องจากราบไปกับตัว คิ้วกว้างสีน้ำตาลแกมเหลือง อกมีลายขีดบาง ๆ หนาแน่น ขอบขนปีกบินสีน้ำตาลแดงเล็กน้อย ขณะเกาะเห็นเป็นสีน้ำตาลแดงน้อยกว่านกจาบฝนอื่น ๆ ขณะบินเห็นขนปีกบินสีคล้ำ ขนหางคู่นอกสีน้ำตาลแกมเหลืองอ่อน', 'ทุ่งข้าวศรีสัชนาลัย จังหวัดสุโขทัย', '42'),
(159, 'นกปรอดหัวสีเขม่า', 'Sooty-headed Bulbul', 'Pycnonotus aurigaster', 'หัวด้านบนดำมีหงอนสั้นเป็นสัน แก้มและคางเทาแกมขาว ลำตัวด้านบนน้ำตาลแกมเทา ลำตัวด้านล่างเทา ตะโพกขาว หางดำปลายขาวเป็นจุดเด่นขณะบิน มีภาวะขนสองแบบคือ ก้นแดงและก้นเหลือง หรือบางตัวอาจเป็นสีส้ม', 'ตำบลวังชมภู อำเภอเมืองเพชรบูรณ์', '43'),
(160, 'นกปรอดหน้านวล', 'Yellow-vented Bulbul', 'Pycnonotus goiavier', 'หัวมีหงอนสั้น ๆ หน้าผากถึงท้ายทอยดำ คิ้วกว้างสีขาว แถบตาดำ คอขาว ลำตัวด้านบนน้ำตาลแกมเทา อกและท้องน้ำอ่อนมีลายขีดจาง ๆ ก้นเหลือง', 'อำเภอเมืองจังหวัดพิษณุโลก', '43'),
(161, 'นกปรอดสวน', 'Streak-eared Bulbul', 'Pycnonotus conradi', 'ขนคลุมหูมีขีดสีขาวแกมเทาชัดเจน ตาสีเทา ขนลำตัว หลัง และปีกสีน้ำตาล คอสีอ่อน ก้นน้ำตาลอ่อนแกมเหลือง', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '43'),
(162, 'นกปรอดคอลาย', 'Stripe-throated Bulbul', 'Pycnonotus finlaysoni', 'แถบตาดำ หน้าผาก หน้า และคอถึงอกตอนบนมีลายขีดสีเหลืองกระจายชัดเจน หัวและลตัวด้านบนน้ำตาลแกมเขียว ปีกเขียวไพลแกมเหลือง ก้นเหลือง ท้องสีน้ำตาลอ่อน', 'อุทยานแห่งชาติศรีสัชนาลัย จังหวัดสุโขทัย', '43'),
(163, 'นกนางแอ่นทรายสร้อยคอดำ', 'Collared Sand Martin ', 'Riparia riparia', 'ขนาดเล็กกว่านกนางแอ่นบ้าน ขนลำตัวด้านบนน้ำตาลแกมเทา ลำตัวด้านล่างขาวมีแถบสีน้ำตาลแกมเทาพาดอก', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '44'),
(164, 'นกนางแอ่นบ้าน', 'Barn Swallow', 'Hirundo rustica', 'ขนลำตัวด้านบนเหลือบน้ำเงินเป็นมัน หน้าผากและคอน้ำตาลแดงเข้มมีแถบดำเหลือบน้ำเงินพาดอก ลำตัวด้านล่างและขนคลุมใต้ปีกขาว หางแฉกลึก ขนหางคู่นอกยาวที่สุด และจะยาวมากขึ้นในช่วงฤดูผสมพันธุ์ ขนหางแต่ละเส้นยกเว้นคู่ในสุดมีจุดสีขาว นกวัยอ่อน : ขนลำตัวด้านบนดำแกมน้ำตาล แถบคาดอกดำ หน้าผากและคอน้ำตาล', 'เป็นนกที่อพยพเข้ามาในฤดูหนาว', '44'),
(165, 'นกนางแอ่นลาย', 'Striated Swallow', 'Cecropis striolata', 'คล้ายนกนางแอ่นตะโพกแดงมาก แต่ขนาดใหญ่กว่า กระหม่อมและท้ายทอยดำเหลือบน้ำเงินต่างจากนกนางแอ่นตะโพกแดงที่มีสีน้ำตาลแดงแกมส้มที่ข้างคอไม่ต่อเนื่องกัน ตะโพกสีน้ำตาลแดงเข้มมีลายขีดหนากว่า ลำตัวด้านล่างขาว มีลายขีดสีดำหนาและชัดเจนกว่า', 'อุทยานแห่งชาติภูหินร่องกล้า จังหวัดพิษณุโลก', '44'),
(166, 'นกนางแอ่นตะโพกแดง', 'Red-rumped Swallow', 'Cecropis daurica', 'จำแนกจากนกนางแอ่นบ้านด้วยขนาดใหญ่กว่า กระหม่อมดำเหลือบน้ำเงิน ข้างคอต่อเนื่องกับท้ายทอยน้ำตาลแดงแกมส้ม ตะโพกสีน้ำตาลแดงแกมส้ม ตอนท้ายสีจางกว่าเล็กน้อย มีลายขีดดำกระจาย แก้ม คอและลำตัวด้านล่างขาว มีลายขีดบาง ๆ กระจาย หนาแน่นที่คอและอก หางแฉกลึก ไม่มีจุดขาวที่ขนหาง ขนหางคู่นอกยาวมาก นกวัยอ่อน : สีน้ำตาลแดงจางกว่า ขนลำตัวแกมน้ำตาลมากกว่า', 'ทุ่งข้าวศรีสัชนาลัย จังหวัดสุโขทัย', '44'),
(167, 'นกนางแอ่นมาตินพันธุ์เอเชียใต้', 'Asian House Martin', 'Delichon dasypus', 'คล้ายนกนางแอ่นมาตินพันธุ์ไซบีเรีย แต่แถบตะโพกขาวแคบกว่า ลำตัวด้านล่างขาว อกและสีข้างแกมเทาเล็กน้อย ขนคลุมใต้ปีกบินดำ แตกต่างจากขนปีกบินสีเทาแกมขาว', 'อุทยานแห่งชาติคลองวังเจ้า จังหวัดกำแพงเพชร', '44'),
(168, 'นกกระจิ๊ดสีคล้ำ', 'Dusky Warbler', 'Phylloscopus fuscatus', 'ปากล่างสีอ่อน คิ้วยาวสีขาวและแกมน้ำตาลส้มทางด้านท้าย ขนคลุมหูแกมน้ำตาลส้มเล็กน้อย ขนลำตัวด้านบนน้ำตาลแกมเขียวมะกอก ขนลำตัวด้านล่างขาวขุ่นแกมสีน้ำตาลเหลืองที่ข้างอก สีข้าง และก้น คล้ายนกกระจิ๊ดปากหนามาก แต่ขนาดเล็กกว่า ปากบางกว่าเล็กน้อย ก้นและท้องสีไม่แตกต่างกัน', 'คลองหนองหลวง จังหวัดพิษณุโลก', '45'),
(169, 'นกกระจิ๊ดปากหนา', 'Radde\'s Warbler', 'Phylloscopus schwarzi', 'คิ้วยาวสีขาว หัวคิ้วแกมน้ำตาลเหลือง ขนาดใหญ่กว่านกระจิ๊ดสีน้ำตาลชนิดอื่น ปากหนา แข้งและตีนหนากว่านกกระจิ๊ดสีคล้ำและนกกระจิ๊ดอกลายเหลือง ต่างจากนกกระจิีดสีคล้ำที่ก้นสีน้ำตาลแกมเข้มแตกต่างจากท้อง แข้งและตีนสีชมพูเข้ม หรือส้มแกมเหลืองคล้ำ', 'อุทยานแห่งชาติคลองวังเจ้า จังหวัดกำแพงเพชร', '45'),
(170, 'นกกระจิ๊ดธรรมดา', 'Yellow-browed Leaf Warbler', 'Phylloscopus inornatus', 'ไม่มีแถบกระหม่อมและแถบตะโพก ปากล่างเหลืองส้ม คิ้วยาวสีเหลืองแกมขาว แถบตาสีคล้ำ ปีกมีแถบ 2 เส้น ปลายขนโคนปีกสีขาวกว้างเห็นเป็นลายชัดเจน ขนลำตัวด้านบนเขียวคล้ำ ลำตัวด้านล่างขาวหรือแกมเหลืองอ่อน', 'อุทยานแห่งชาติน้ำหนาว จังหวัดเพชรบูรณ์', '45'),
(171, 'นกกระจ้อยวงตาสีทองหางสีเรียบ', 'Alstrom\'s Warbler', 'Seicercus soror', 'สีขาวที่หางน้อยกว่าชนิดอื่น เห็นชัดเฉพาะปลายหาง 2 คู่นอก หน้าผากเขียวแกมเหลือง คิ้วสีดำแกมเทาไม่ถึงหัวตาทำให้ดูสั้นกว่าชนิดอื่น สีเทากลางหัวจางกว่านกกระจ้อยวงตาสีทองชนิดอื่น', 'ตำบลบ้านหนองตูม จังหวัดสุโขทัย', '45'),
(172, 'นกกระจิ๊ดขาสีเนื้อ', 'Pale-legged Leaf Warbler', 'Phylloscopus tenellipes', 'คิ้วยาวสีขาวหรือขาวแกมเหลือง หัวและท้ายทอยเทา แตกต่างจากหลังและลำตัวด้านบนสีเขียวคล้ำ ตะโพกสีอ่อนกว่าส่วนอื่นเล็ก ปีกมีแถบแคบ ๆ 2 แถบ หรืออาจเห็นชัดเพียงแถบเดียว ขาสีชมพูอ่อนหรือสีเนื้อ', 'อาศัยอยู่ในพื้นที่ภาคเหนือตอนล่าง', '45'),
(173, 'นกกระจิ๊ดคิ้วดำท้องเหลือง', 'Sulphur-breasted Leaf Warbler', 'Phylloscopus ricketti', 'คล้ายนกกระจิ๊ดคิ้วดำท้องขาว แต่ลำตัวด้านล่างเหลืองตลอด คล้ายนกแว่นตาเหลือง แต่ไม่มีวงรอบตาสีเหลือง', 'อุทยานแห่งชาติน้ำหนาว จังหวัดเพชรบูรณ์', '45'),
(174, 'นกกระจิ๊ดหัวมงกุฎ', 'Eastern Crowned Leaf Warbler', 'Phylloscopus coronatus', 'ปากยาว ปากล่างสีส้มเด่นชัด คิ้วขาวหรือขาวแกมเหลือง กระหม่อมสีเทามีแถบกลางสีเขียวแกมเหลือง แถบตาสีคล้ำ ลำตัวด้านบนเขียวแกมเหลืองคล้ำหรือแกมน้ำตาลอ่อน แถบปีกแคบ ๆ สีเหลืองอ่อน ลำตัวด้านล่างขาว ก้นสีเหลืองอ่อนเป็นจุดเด่น แข้งและตีนสีแดงคล้ำ', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '45'),
(175, 'นกกระจิ๊ดเขียวปีกสองแถบ', 'Two-barred Leaf Warbler', 'Phylloscopus plumbeitarsus', 'คล้ายนกกระจิ๊ดเขียวคล้ำมาก แต่แถบปีกเห็น 2 เส้นชัดเจนสีขาวแกมเหลือง แต่ในช่วงขนเก่าแถบสั้นอาจหายไป แข้งและตีนสีชมพูอ่อนหรือเทาแกมชมพู', 'ทุ่งข้าวโป่งแดง จังหวัดตาก', '45'),
(176, 'นกกระจิ๊ดขั้วโลกเหนือ', 'Arctic Leaf Warbler', 'Phylloscopus borealis', 'ปากยาว ปากล่างสีชมพูหรือส้มปลายดำ คิ้วยาวหรือแกมเหลืองอ่อน แถบตาสีคล้ำ หัวและลำตัวด้านบนเขียวคล้ำ แถบปีกแคบยาวสีขาวแกมเหลืองอ่อน หรือน้ำตาลอ่อน บางตัวอาจห็น 2 แถบ ลำตัวด้านล่างขาว อกและข้างอกแกมเทา', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '45'),
(177, 'นกกระจ้อยวงตาสีทองพันธุ์จีน', 'Martens\'s Warbler', 'Seicercus omeiensis', 'ลักษณะสีขาวที่ปลายหาง 2 คู่นอกอยู่ระหว่างนกกระจ้อยวงตาสีทองหัวเทากับนกกระจ้อยวงตาสีทองหางสีเรียบ บางครั้งอาจมีแต้มขาวที่ปลายหางคู่ต่อไป ซึ่งไม่พบในนกระจ้อยวงตาสีทองหางสีเรียบ แต่พบประจำในนกกระจ้อยวงตาสีทองหัวเทา', 'ศูนย์บริการนักท่องเที่ยวอุทยานแห่งชาติแม่วงก์ จังหวัดกำแพงเพชร', '45'),
(178, 'นกพงใหญ่พันธุ์ญี่ปุ่น', 'Oriental Reed Warbler', 'Acrocephalus orientalis', 'ปากล่างสีเนื้อแกมส้มปลายคล้ำ คิ้วยาวสีขาว แถบตาดำ ขนลำตัวด้านบนน้ำตาลแกมเขียวไพล คอและลำตัวด้านล่างขาว อกมีขีดสีเทาจาง ๆ สีข้างและก้นน้ำตาลแกมเหลืองเข้ม ปลายหางมีแต้มขาวหรือขาวแกมน้ำตาล ขณะเกาะขนปลายปีกยาวกว่าเมื่อเทียบกับปลายขนโคนปีก', 'อำเภอตะพานหิน จังหวัดพิจิตร', '46'),
(179, 'นกพงปากหนา', 'Thick-billed Warbler', 'Arundinax aedon', 'ปากสั้นและหนากว่านกพงใหญ่พันธุ์ญี่ปุ่น ไม่มีคิ้วขาวหรือสีน้ำตาลอ่อนและแถบตาสีเข้มแต่หัวตาและรอบตาสีน้ำตาลอ่อนกว่าส่วนอื่น คอขาว หัวและลำตัวด้านบนน้ำตาล ปีกและสีข้างเป็นสีน้ำตาลแดงแกมส้ม', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '46'),
(180, 'นกพงคิ้วดำ', 'Black-browed Reed Warbler', 'Acrocephalus bistrigiceps', 'คิ้วยาวน้ำตาลแกมเหลืองมีแถบข้างหัวเหนือคิ้วดำชัดและกว้างกว่านกพงชนิดอื่น ๆ หัวและลำตัวด้านบนเขียวมะกอก ตะโพกแกมน้ำตาลแดงกว่าส่วนอื่น ลำตัวด้านล่างขาว มีสีน้ำตาลแกมเหลืองที่อกและสีข้าง', 'คลองหนองหลวง จังหวัดพิษณุโลก', '46'),
(181, 'นกพงตั้กแตนท้ายทอยสีเทา', 'Pallas\'s Grasshopper Warbler', 'Locustella certhiola', 'คิ้วยาวสีขาวและขนาดใหญ่กว่านกตั๊กแตนอกลาย ขนลำตัวแกมน้ำตาลแดงมากกว่าโดยเฉพาะตะโพก กระหม่อมและหลังสีน้ำตาลเข้มแกมเทามีลายขีดดำกระจาย ตะโพกน้ำตาลแดงมีลายขีดดำ หางน้ำตาลเข้มแกมเทามีลายขวางสีดำจาง ๆ ปลายขาว ลำตัวด้านล่างน้ำตาลแกมเขียว คอและกลางื้องแกมขาว', 'หุบเขาทะเลแก้ว จังหวัดพิษณุโลก', '47'),
(182, 'นกพงตั้กแตนอกลาย', 'Lanceolated Warbler', 'Locustella lanceolata', 'คล้ายนกพงตั๊กแตนท้ายทอยสีเทา แต่ขนาดเล็กกว่า คิ้วยาวสีน้ำตาลอ่อน ขนลำตัวสีน้ำตาลแกมเขียวไพล มีลายขีดสีดำทั้งลำตัวด้านบนและด้านล่าง ขนคลุมใต้หางยาวมากมีลายขีดดำกระจาย', 'เขตห้ามล่าสัตว์ป่าเขาน้อย-เขาประดู่ จังหวัดพิษณุโลก', '47'),
(183, 'นกยอดข้าวหางแพนลาย', 'Zitting Cisticola', 'Cisticola juncidis', 'คิ้วยาวสีขาว ขนลำตัวด้านบนน้ำตาลแกมเหลือง มีลายหนาสีดำกระจาย ตะโพกน้ำตาลแดง หางน้ำตาลปลายดำ-ขาว ลำตัวด้านล่างขาว  อกและสีข้างน้ำตาลเหลืองหรือแกมน้ำตาลแดง ขนชุดผสมพันธุ์ : ลายดำบนกระหม่อมและหลังชัดเจนขึ้น หางสั้นลงเล็กน้อย สีน้ำตาลแดงมากขึ้น (ดูนกยอดข้าวหางแพนหัวแดง)', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '48'),
(184, 'นกยอดข้าวหางแพนหัวแดง', 'Bright-capped Cisticola', 'Cisticola exilis', 'คล้ายนกยอดข้าวหางแพนลาย แต่คิ้ว ข้างคอ และท้ายทอยน้ำตาลแดงกว่า หางยาวกว่า ปลายหางน้ำตาลแกมขาว ตัวผู้ขนชุดผสมพันธุ์ : หัวไม่มีลาย สีส้มแกมน้ำตาลสดใส ลำตัวด้านล่างส้มแกมน้ำตาลชัดเจนมากขึ้น หางสั้นลง, ตัวเมียขนชุดผสมพันธุ์ : คล้ายขนนอกฤดูผสมพันธุ์ แต่ตะโพกไม่มีลายสีคล้ำ', 'ตำบลลานดอกไม้ จังหวัดกําแพงเพชร', '48'),
(185, 'นกกระจิบหญ้าสีข้างแดง', 'Rufescent Prinia', 'Prinia rufescens', 'หัวเทา คิ้วยาวสีขาวเลยหางตา ลำตัวด้านบนน้ำตาลแกมเขียวไพล ขนปีกบินน้ำตาลแดง คอขาว ลำตัวด้านล่างน้ำตาลอ่อนแกมเหลือง ปลายหางด้านล่างขลิบดำและขาว ขนชุดผสมพันธุ์ : หัวถึงท้ายทอยเทาเข้มขึ้น', 'ศูนย์บริการนักท่องเที่ยวอุทยานแห่งชาติแม่วงก์ จังหวัดกำแพงเพชร', '48'),
(186, 'นกกระจิบหญ้าอกเทา', 'Grey-breasted Prinia', 'Prinia hodgsonii', 'คล้ายนกกระจิบหญ้าสีข้างแดง แต่คิ้วขาวเฉพาะหัวตา ปากบางกว่า ปีกสีน้ำตาลแกมแดงน้อยกว่า ขนชุดผสมพันธุ์ : หัวเทาเข้ม ไม่มีคิ้วขาว คอขาว มีแถบเทาเข้มพาดอก', 'หาดเสี้ยวศรีสัชนาลัย จังหวัดสุโขทัย', '48'),
(187, 'นกกระจิบหญ้าสีเรียบ', 'Plain Prinia', 'Prinia inornata', 'คิ้วยาวสีขาวหรือขาวแกมเหลืองชัดเจน หัวและลำตัวด้านบนน้ำตาล ลำตัวด้านล่างขาวแกมน้ำตาลเหลือง ขนหางใหญ่กว่านกกระจิบหญ้าอกเทาและนกกระจิบหญ้าสีข้างแดง ปลายหางด้านล่างขลิบน้ำตาลเข้มและขาว ชนิดย่อย P.i.blanfordi ลำตัวด้านล่างและคิ้วสีน้ำตาลแกมส้ม', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '48'),
(188, 'นกกระจิบหญ้าท้องเหลือง', 'Yellow-bellied Prinia', 'Prinia flaviventris', 'ท้องถึงก้นเหลืองสดเป็นจุดเด่น หัวเทา ลำตัวด้านบนเขียวไพลแกมเทา มีคิ้วขาวสั้น ๆ คอขาว', 'บึงราชนก จังหวัดพิษณุโลก', '48'),
(189, 'นกกระจิบคอดำ', 'Dark-necked Tailorbird', 'Orthotomus atrogularis', 'คล้ายนกกระจิบธรรมดา แต่ก้นเหลืองทั้งสองเพศ ตัวผู้ : หน้าผากถึง้ทายทอยน้ำตาลแดง ข้างคอและอกดำแกมเทา ลำตัวด้านบนเขียวแกมเหลืองสดกว่านกกระจิบธรรมดา ไม่มีหางคู่กลางยาวแหลม, ตัวเมีย : ข้างคอและอกไม่มีแถบดำ', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '48'),
(190, 'นกกระจิบธรรมดา', 'Common Tailorbird', 'Orthotomus sutorius', 'หน้าผากและกระหม่อมน้ำตาลแดง คิ้วและหน้าขาวแกมทา ลำตัวด้านบนเขียวคล้ำ ลำตัวด้านล่างขาว บางครั้งใต้คอเห็นเป็นสีดำโดยเฉพาะขณะร้อง คล้ายนกกระจิบคอดำ แต่ก้นขาว ตัวผู้ขนชุดผสมพันธุ์ : หางคู่กลางยาวมากและปลายแหลม', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '48'),
(191, 'นกกินแมลงกระหม่อมแดง', 'Chestnut-capped Babbler', 'Timalia pileata', 'ปากหนาสีดำ กระหม่อมถึงท้ายทอยน้ำตาลแดง คิ้วขาว แถบตาสั้น ๆ สีดำ ข้างหัว คอและอกขาวมีลายขีดดำเป็นเส้นเล็ก ๆ กระจาย ท้องน้ำตาลแกมเหลือง ลำตัวด้านบนน้ำตาล หางค่อนข้างยาวมีลายบั้งดำจาง ๆ นกวัยอ่อน : กระหม่อมสีน้ำตาล ปากสีเนื้อแกมดำ', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '49'),
(192, 'นกกินแมลงตาเหลือง', 'Yellow-eyed Babbler', 'Chrysomma sinense', 'คล้ายนกกินแมลงกระหม่อมแดง แต่หางยาว ปากดำ หนังรอบตาส้มเหลืองเด่นชัด ลำตัวด้านบนน้ำตาล ปีกและหางแกมน้ำตาลแดง หัวตา ข้างหัว คอถึงท้องขาว ท้องตอนล่างถึงก้นอาจแซมน้ำตาลเหลือง', 'หาดเสี้ยวศรีสัชนาลัย จังหวัดสุโขทัย', '50'),
(193, 'นกแว่นตาขาวสีข้างแดง', 'Chestnut-flanked White-eye', 'Zosterops erythropleurus', 'ต่างจากนกแว่นตาขาวอื่น ๆ ที่สีข้างมีแถบสีน้ำตาลแดง แต่บางตัวอาจเป็นเพียงแถบสีชมพูจาง ๆ ไม่ชัดเจน หน้าผากเขียวแกมเหลืองเหมือนส่วนอื่น ๆ ของลำตัวด้านบน อกและท้องเทา', 'เขตรักษาพันธุ์สัตว์ป่าภูหลวง จังหวัดเลย', '51'),
(194, 'นกแว่นตาขาวหลังเขียว', 'Swinhoe\'s White-eye', 'Zosterops simplex', 'คล้ายนกแว่นตาขาวสีทองมาก แต่ลำตัวด้านบนเขียวแกมเหลืองเข้มกว่าตัดกับหน้าผากและหัวตาเหลืองเป็นบริเวณแคบ ๆ ส่วนสีเหลืองของลำตัวด้านล่างไม่เด่นชัด จำแนกง่ายจากท้องเทาตลอดไม่มีแถบสีเหลืองหรือสีอ่อนแบ่งกลางท้อง ก้นเหลือง', 'ทุ่งข้าวโป่งแดง จังหวัดตาก', '51'),
(195, 'นกเอี้ยงพราหมณ์', 'Brahminy Starling', 'Sturnia pagodarum', 'ตัวและหน้าอกมีสีน้ำตาล หัวสีดำ ปากสีเหลือง ส่วนปีกและหางมีสีเทาดำ', 'ตำบลแหลมผักเบี้ย จังหวัดเพชรบุรี', '52'),
(196, 'นกกิ้งโครงแกลบหลังม่วงดำ', 'Purple-backed Starling', 'Agropsar sturninus', 'ตัวผู้ : หัวและลำตัวด้านล่างเทา ท้ายทอยเป็นแถบค่อนข้างกลมสีดำแกมม่วง ลำตัวด้านบนสีดำแกมม่วง ปีกมีแถบขวางสีขาวและน้ำตาลเหลือง ปากดำโคนปากสีอ่อน ตะโพกและก้นสีน้ำตาลอ่อน', 'บึงสีไฟ จังหวัดพิจิตร', '52'),
(197, 'นกกิ้งโครงแกลบปีกขาว', 'White-shouldered Starling', 'Sturnia sinensis', 'ตัวผู้ : ขนลำตัวเทาแกมขาว ตัดกับปีกและหางดำ โคนปีกขาวเป็นแถบกว้าง ปากเทาแกมฟ้า, ตัวเมีย : ส่วนหัวแกมน้ำตาลมากกว่าตัวผู้ แถบขาวที่ปีกเล็กกว่า, นกวัยอ่อน : แกมน้ำตาลมากกว่าตัวเมีย ปีกไม่มีแถบขาว', 'ตำบลทุ่งหลวง จังหวัดสุโขทัย', '52'),
(198, 'นกกิ้งโครงพันธุ์ยุโรป', 'Common Starling', 'Sturnus vulgaris', 'ปากเรียวแหลมสีดำ ขนลำตัวดำเป็นมันเหลือบเขียวมีจุดขาวเล็กกระจายทั่วทั้งตัว แข้งและตีนแดงคล้ำ ขนชุดผสมพันธุ์ : ปากเหลือง ขนลำตัวเหลือบม่วงมากขึ้น ลำตัวด้านบนเหลือบเขียว จุดขาวบนลำตัวน้อยลง, นกวัยอ่อน : หัวแกมน้ำตาล', 'บึงกะโล่ จังหวัดอุตรดิตถ์', '52'),
(199, 'นกกิ้งโครงแกลบหัวเทา', 'Chestnut-tailed Starling', 'Sturnia malabarica', 'ปากฟ้าแกมเทา ปลายปากส้ม หัวเทาแกมขาว หลังเทาเข้ม ขนปีกบินดำมีแถบขาวเล็ก ๆ ที่หัวปีก เห็นชัดขณะบิน อกและลำตัวด้านล่างน้ำตาลอ่อนแกมเทา สีข้างและขนหางคู่นอกน้ำตาลแดง ชนิดย่อย malabaricus ลำตัวด้านล่างน้ำตาลแดง', 'มอนเทอร์เรย์พาร์ค รีสอร์ท ตำบลเขาทราย จังหวัดพิจิตร', '52'),
(200, 'นกกิ้งโครงแกลบแก้มสีน้ำตาลแดง', 'Chestnut-cheeked Starling', 'Agropsar philippensis', 'คล้ายนกกิ้งโครงแกลบหลังม่วงดำ ตัวผู้ : หัวสีน้ำตาลอ่อนหรือน้ำตาลแกมเหลือง แก้มถึงข้างคอเป็นแถบเลอะสีน้ำตาลแดงเข้ม อกและสีข้างเทาเข้ม ท้องขาว หลังและปีกดำเหลือบม่วง ปีกมีแถบขาวคาดชัดเจน ตะโพกและก้นสีครีมถึงขาว, ตัวเมีย : คล้ายนกกิ้งโครงแกลบหลังม่วงดำตัวเมียมาก แต่หัวสีจางกว่า และแถบขาวที่ปีกต่างกัน', 'จังหวัดชุมพร', '52'),
(201, 'นกกิ้งโครงสีกุหลาบ', 'Rosy Starling', 'Pastor roseus', 'ปากหนาและอวบกว่านกกิ้งโครงชนิดอื่น ปากเหลืองปลายสีคล้ำ หัวและคอดำ ท้ายทอยมีหงอนสั้นๆ ขนลำตัวสีชมพูอ่อน ปีก ก้น และหางดำ ก้นมีลายเกล็ดสีชมพู ชุดขนฤดูผสมพันธุ์ : ปากชมพู โคนปากดำน้ หัวดำเข้มเหลือบเขียวตัดกับขนลำตัวสีชมพูสดใสขึ้น แข้งและตีนชมพูเข้ม, นกวัยอ่อน : ปากเหลือง ลำตัวสีน้ำตาลอ่อน ปีกและหางสีน้ำตาลเข้ม', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '52'),
(202, 'นกเอี้ยงด่าง', 'Asian Pied Myna', 'Gracupica contra', 'ปากแหลม สีเหลืองสด โคนปากและหนังรอบตาแดงเข้มแกมชมพู หัว คอ อก ลำตัวด้านบน และหางดำ หน้าผากและขนคลุมหูขาว ลำตัวด้านล่างขาว ปีกมีแถบขาว แข้งและตีนเหลือง', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '52'),
(203, 'นกกิ้งโครงคอดำ', 'Black-collared Myna', 'Gracupica nigricollis', 'ขนาดใหญ่ ปากดำ หัวเหลืองนวล หนังรอบตาเหลือง รอบคอและอกเป็นแถบดำใหญ่ ลำตัวด้านบนดำแกมน้ำตาล มีแถบขาวแคบ ๆ เห็นชัดขณะบิน ลำตัวด้านล่างขาว นกวัยอ่อน : หัว อก และลำตัวด้านบนน้ำตาล หนังรอบตาเหลืองคล้ำ', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '52'),
(204, 'นกเอี้ยงสาริกา', 'Common Myna', 'Acridotheres tristis', 'หัวและอกดำแกมน้ำตาล ปากและหนังรอบตาเหลือง ลำตัวน้ำตาลเข้ม กลางท้องและก้นขาว ขนปีกบินดำมีแถบขาว ขณะบินใต้ปีกขาว ปีกบนมีแถบขาวเป็นวงใหญ่ แข้งและตีนเหลือง นกวัยอ่อน : หัวและอกน้ำตาล บางตัวหัวอาจล้านเป็นหนังสีเหลือง', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '52'),
(205, 'นกเอี้ยงหงอน', 'White-vented Myna', 'Acridotheres grandis', 'ขนลำตัวดำ ตาน้ำตาลแดงเข้มเกือบดำ ปากเหลือง หน้าผากมีหงอนยาวตั้งเด่นชัด ปีกมีแถบขาวเห็นเป็นวงใหญ่ขณะบิน ก้นขาว แข้งและตีนเหลือง นกวัยอ่อน : หงอนสั้น ขนลำตัวแกมน้ำตาล', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '52'),
(206, 'นกเดินดงหัวสีส้ม', 'Orange-headed Thrush', 'Geokichla citrina', 'ตัวผู้ : หัวและลำตัวด้านล่างสีส้ม ก้นขาว ลำตัวด้านบนเทแกมฟ้า, ตัวเมีย : สีอ่อนกว่าเล็กน้อย ลำตัวด้านบนและปีกเทาแกมเขียว หลังตามีแถบสีคล้ำ ชนิดย่อย gibsonhilli ประจำถิ่นทางภาคตะวันตก มีแถบขาวเล็ก ๆ ใกล้หัวปีก ชนิดย่อย innotata อพยพมาทำรังวางไข่ทางภาคเหนือและภาคตะวันออกเฉียงเหนือ จะมีสีปีกเรียบ ๆ', 'อุทยานแห่งชาติศรีสัชนาลัย จังหวัดสุโขทัย', '53'),
(207, 'นกเดินดงสีเทาดำ', 'Siberian Thrush', 'Geokichla sibirica', 'ตัวผู้ : คิ้วยาวสีขาวตัดกับลำตัวสีเทาแกมน้ำเงินเข้ม ลำตัวด้านล่างสีจางกว่าเล็กน้อย ท้องขาว ก้นเทาเข้มมีลายขาว, นกตัวผู้วัยอ่อน : ลำตัวสีเทาแกมน้ำตาล คอสีน้ำตาลอ่อน มีลายเกล็ดที่ท้อง, ตัวเมีย : ลำตัวด้านบนสีน้ำตาล คิ้วสีน้ำตาลอ่อนแกมขาวยาวโค้งเป็นวงล้อม ขนคลุมหูสีน้ำตาลเข้ม ท้องน้ำตาลอ่อนมีลายเกล็ดสีขาวแกมเทา ก้นขาว มีลายน้ำตาล ปลายหางขาว', 'ศูนย์บริการนักท่องเที่ยวอุทยานแห่งชาติแม่วงก์ จังหวัดกำแพงเพชร', '53'),
(208, 'นกเดินดงลายเสือใหญ่', 'White\'s Thrush', 'Zoothera aurea', 'คล้ายนกเดินดงลายเสือมากและยากต่อการจำแนก แต่ลำตัวมีขนาดใหญ่กว่า ปากยาวกว่า หน้าสีขาวชัดเจนมากกว่า วงสีขาวรอบตาใหญ่และชัดเจนกว่า ลำตัวด้านบนสีจางและแกมเทามากกว่า ลายเกล็ดบนหลังดูกลมและใหญ่กว่า', 'จุดชมวิวขุนน้ำเย็นอุทยานแห่งชาติแม่วงก์ จังหวัดกำแพงเพชร', '53'),
(209, 'นกเดินดงสีคล้ำ', 'Eyebrowed Thrush', 'Turdus obscurus', 'ตัวผู้ : สันปากบนดำ ปากล่างสีส้ม หัวเทา ปถบตาดำ คิ้วและแถบใต้ตาขาวชัดเจน ลำตัวด้านบนน้ำตาล อกและลำตัวด้านล่างส้มแกมน้ำตาล กลางท้องถึงก้นขาว, ตัวเมีย :  คล้ายตัวผู้ แต่หัวสีน้ำตาล คอขาว แถบหนวดดำ สีส้มที่อกและลำตัวด้านล่างจางกว่า', 'ศูนย์บริการนักท่องเที่ยวอุทยานแห่งชาติแม่วงก์ จังหวัดกำแพงเพชร', '53'),
(210, 'นกคอมรกต', 'Bluethroat', 'Luscinia svecica', 'ตัวผู้ : คอและอกตอนบนน้ำเงินสดล้อมรอบด้วยส้มแดงรูปล่างและขนาดแตกต่างกันในนกแต่ละตัว มีแถบดำคั่นแบ่งจากอกตอนล่างสีส้ม คิ้วยาวสีขาวหรือแกมน้ำตาลอ่อน หัวและลำตัวด้านบนน้ำตาล โคนหางส้มแกมน้ำตาลแดงเด่นชัด, ตัวเมีย : ไม่มีสีน้ำเงินและแถบส้มแดงที่คอ แถบหนวดขาวมีเส้นดำต่อเนื่องกับอก หางดำโคนน้ำตาลแดง', 'หุบเขาทะเลแก้ว จังหวัดพิษณุโลก', '54'),
(211, 'นกคอทับทิม', 'Siberian Rubythroat', 'Calliope calliope', 'ตัวผู้ : คอแดงสด วาว ปากและหน้าดำ คิ้วและแถบหนวดขาวชัดเจน หัวและลำตัวด้านบนน้ำตาล อกเทา ลำตัวด้านล่างน้ำตาลแกมส้ม, ตัวเมีย : ไม่มีคอสีแดง แถบน้ำตาลคล้ำ คิ้วและแถบหนวดขาว มีเส้นดำจาง ๆ คั่นกับคอขาว', 'บึงบอระเพ็ดเขตห้ามล่าสัตว์ จังหวัดนครสวรรค์', '54'),
(212, 'นกกางเขนบ้าน', 'Oriental Magpie Robin', 'Copsychus saularis', 'ตัวผู้ : หัว อก และลำตัวด้านบนดำเป็นมันเล็กน้อย ปีกมีแถบขาว ขอบหางคู่นอก ๆ ขาว ลำตัวด้านล่างขาว, ตัวเมีย : คล้ายตัวผู้ แต่หัวและอกเทาเข้ม ลำตัวด้านบนดำไม่เหลือบเป็นมัน', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '54'),
(213, 'นกเขนน้อยไซบีเรีย', 'Siberain Blue Robin', 'Larvivora cyane', 'หางค่อนข้างสั้น แข้งและตีนชมพูหรือสีเนื้อ ตัวผู้ : ปากชมพู สันปากบนดำ หัว ลำตัวด้านบน และหางฟ้าเข้ม แถบตาดำถึงข้างอก ตัดกับลำตัวด้านล่างขาวสะอาด, ตัวเมีย : วงรอบตาน้ำตาลอ่อน หัวและลำตัวด้านบนเทาแกมน้ำตาล ตะโพกและหางฟ้า ข้างแก้ม อก และสีข้างน้ำตาลแกมเหลือง คอและท้องขาว, ตัวผู้วัยอ่อน : คล้ายตัวเมีย แต่หลังและไหล่ฟ้าคล้ำ ปีกมีลายจุดจากปลายขนน้ำตาลอ่อน, ตัวเมียวัยอ่อน : ตะโพกและหางน้ำตาล อกมีลายเกล็ดสีคล้ำ', 'อุทยานแห่งชาติภูสวนทราย จังหวัดเลย', '54'),
(214, 'นกกางเขนดง', 'White-rumped Shama', 'Kittacincla malabarica', 'ตัวผู้ : หัว อก และลำตัวด้านบนดำเป็นมัน ตัดกับลำตัวด้านล่างน้ำตาลแดงแกมส้มเข้ม และตะโพกขาว หางยาวขอบหางคู่นอก ๆ ขาว, ตัวเมีย : คล้ายตัวผู้ แต่หางสั้นกว่า หัว อก และลำตัวด้านบนแกมน้ำตาล ลำตัวด้านล่างส้มแกมน้ำตาลมากกว่า', 'อุทยานแห่งชาติศรีสัชนาลัย จังหวัดสุโขทัย', '54'),
(215, 'นกยอดหญ้าหัวดำ', 'Eastern Stonechat', 'Saxicola maurus', 'ตัวผู้ : หัวและลำตัวน้ำตาลอ่อน ส่วนหัวแกมสีดำที่หัวตา ขนคลุมหู คาง และอกตอนบน หลังแกมน้ำตาลเข้มแกมเทามีลายสีคล้ำ อกน้ำตาลแดง, ตัวผู้ขนชุดผสมพันธุ์ : หัวและคอดำ ลำตัวด้านบนดำมีลายน้ำตาลจาง ๆ ข้างคอและแถบปีกขาว ตะโพกขาวปลายขนคลุม ตะโพกน้ำตาลแดง ลำตัวด้านล่างน้ำตาลแกมส้ม อกน้ำตาลแดงเข้มขึ้น หางดำ, ตัวเมีย : คล้ายตัวผู้นอกฤดูผสมพันธุ์ แต่คอและก้นแกมขาว ตะโพกน้ำตาลอ่อนแกมเหลือง', 'คลองหนองหลวง จังหวัดพิษณุโลก', '54'),
(216, 'นกยอดหญ้าสีดำ, นกขี้หมา', 'Pied Bushchat', 'Saxicola caprata', 'ตัวผู้ : ขนลำตัวดำ ตัดกับตะโพกและแถบปีกขาว เห็นชัดขณะบิน, ตัวเมีย : ขนลำตัวน้ำตาลเข้ม ลำตัวด้านบนและอกมีลายขีดสีคล้ำ ตะโพกน้ำตาลแดง', 'ทุ่งข้าวศรีสัชนาลัย จังหวัดสุโขทัย', '54'),
(217, 'นกนิลตวาท้องสีส้มพันธุ์จีน', 'Fujian Niltava', 'Niltava davidi', 'ตัวผู้ : คล้ายนกนิลตวาท้องสีส้มคอดำมาก จำแนกในธรรมชาติได้ยาก แต่เฉพาะหน้าผากและข้างกระหม่อมเป็นสีฟ้าสว่าง หลังสีเข้มกว่าหัว หัวไหล่ไม่มีสีฟ้าสว่าง ลำตัวด้านล่างสีส้มแกมน้ำตาลแดงคล้ำกว่า อกสีเข้มกว่าท้อง ท้องด้านล่างและก้นน้ำตาลอ่อนแกมขาวหรือขาว, ตัวเมีย : คล้ายนกนิลตวาท้องสีส้มคอดำมาก แต่ลำตัวด้านบน ปีก และหางสีหม่นกว่า ไม่แกมน้ำตาลแดง คอด้านล่าง อกด้านบน และสีข้างสีเข้มกว่า ตัดกับแถบขาวคาดคอชัดเจนกว่า', 'อุทยานแห่งชาติดอยภูคา จังหวัดน่าน', '54'),
(218, 'นกกระเบื้องผา', 'Blue Rockthrush', 'Monticola solitarius', 'ตัวผู้ : ลำตัวฟ้าแกมเทา มีลายเกล็ดขาวดำกระจาย ช่วงผสมพันธุ์สีเข้มขึ้น, ตัวเมีย : ขนลำตัวเทาแกมน้ำตาล มีลายเกล็ดหนาแน่นสีเนื้อสลับดำกระจาย', 'อุทยานแห่งชาติภูหินร่องกล้า จังหวัดพิษณุโลก', '54'),
(219, 'นกกระเบื้องคอขาว', 'White-throated Rockthrush', 'Monticola gularis', 'ตัวผู้ : แถบตาดำ กระหม่อมและท้ายทอยฟ้าเข้มเป็นมัน หัวตา หน้า ตะโพก และลำตัวด้านล่างน้ำตาลแดงแกงส้ม กลางคอขาว หลังดำมีลายเกล็ดสีน้ำตาล ปีกน้ำตาลเข้มแกมดำมีแถบขาว, ตัวเมีย : หัวน้ำตาล คอขาว หัวตาน้ำตาลแกมขาว ลำตัวด้านบนมีลายเกล็ด ดำสลับน้ำตาล ปีกไม่มีแถบขาว ลำตัวด้านล่างน้ำตาลแกมเหลือง มีลายเกล็ดดำกระจาย', 'อุทยานแห่งชาติน้ำหนาว จังหวัดเพชรบูรณ์', '54'),
(220, 'นกจับแมลงสีน้ำตาลแดง', 'Ferruginous Flycatcher', 'Muscicapa ferruginea', 'หัวเทาเข้มตัดกับวงรอบตาสีขาวแกมน้ำตาลอ่อน ลำตัวบนเทาแกมน้ำตาลแดง ปีกเทา มีแถบจากขอบขนสีน้ำตาลแดง ตะโพกและโคนหางสีน้ำตาลแดงเด่นชัด อกและลำตัวด้านล่างน้ำตาลแดง ท้องขาว', 'สวนหลวง ร.9 และ อำเภอตะพานหิน จังหวัดพิจิตร', '54'),
(221, 'นกจับแมลงสีคล้ำ', 'Dark-sided Flycatcher', 'Muscicapa sibirica', 'ขณะเกาะปลายปีกยาวใกล้ปลายหางมากกว่าชนิดอื่น ปากสั้นดำ หัวและลำตัวด้านบนเทาแกมน้ำตาล กลางคอและข้างคอยาว ตัดกับอกสีเทาแกมน้ำตาล มีลายขีดที่ท้องและข้างตัวหนาแน่น ท้องตอนล่างและก้นขาว', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '54'),
(222, 'นกจับแมลงสีน้ำตาล', 'Asian Brown Flycatcher', 'Muscicapa dauurica', 'ปากยาวกว่านกจับแมลงสีคล้ำ โคนปากล่างเหลืองหรือสีเนื้อปลายดำ หัวตาและวงตาขาวใหญ่ หัวและลำตัวด้านบนสีน้ำตาลถึงเทาแกมน้ำตาล ปีกมีแถบขาวแคบ ๆ แต่เห็นชัด โดยเฉพาะขอบโคนปีก คอและลำตัวด้านล่างขาวแกมเทา อกมีลายขีดเล็ก ๆ คล้ายนกจับแมลงสีน้ำตาลท้องลาย หรือเลียบไม่มีลาย', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '54'),
(223, 'นกจับแมลงสีฟ้าอกดำ', 'Blue-and-white Flycatcher', 'Cyanoptila cyanomelana', 'ขนาดใหญ่ ปากค่อนข้างสั้น ตัวผู้ : หัวและลำตัวด้านบนฟ้าเข้ม หน้า ข้างคอ และอกดำ อกตอนล่างและท้องขาว, ตัวเมีย : หัว อก และลำตัวด้านบนน้ำตาลแกมเทา อกตอนล่างและท้องขาวตัดกันค่อนข้างชัดเจนกับอกตอนบน, ตัวผู้วัยอ่อน : คล้ายตัวเมีย แต่ปีกและหางฟ้าเข้มแซมสีน้ำตาล', 'โป่งแดง จังหวัดตาก', '54'),
(224, 'นกจับแมลงสีฟ้า', 'Verditer Flycatcher', 'Eumyias thalassinus', 'ตัวผู้ : ปากสั้น หัวตาและแถบตาสั้น ๆ สีดำ หัวและลำตัวฟ้าแกมเขียวเข้ม, ตัวเมีย : สีฟ้าอ่อนแกมสีเทากว่าตัวผู้ หัวตาและแถบตาไม่ชัดเจนเหมือนตัวผู้ แตกต่างจากนกจับแมลงสีฟ้าอ่อนที่ปากสั้นกว่า ก้นฟ้ามีลายขาว', 'เขตรักษาพันธุ์สัตว์ป่าภูหลวง จังหวัดเลย', '54'),
(225, 'นกจับแมลงคอแดง', 'Taiga Flycatcher', 'Ficedula albicilla', 'ปากดำ หัวและลำตัวด้านบนน้ำตาลแกมเทา หางดำ โคนหางคู่นอก ๆ ขาว ลำตัวด้านล่างขาวแกมเทา ตัวผู้ขนชุดผสมพันธุ์ : ใต้คอสีส้มเข้ม อกและท้องเทาเข้มขึ้น พฤติกรรมชอบกระดกหาง', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '54'),
(226, 'นกจับแมลงสีฟ้าท้องขาว', 'Zappey\'s Flycatcher', 'Cyanoptila cumatilis', 'ตัวผู้ : คล้ายนกจับแมลงสีฟ้าอกดำมาก ซึ่งปัจจุบันถูกแยกชนิดออกจากกัน แต่ลำตัวโดยรวมสีน้ำเงินสดกว่าเล็กน้อย อกสีน้ำเงินอมฟ้า, ตัวเมีย : คล้ายตัวเมียของนกจับแมลงสีฟ้าอกดำมาก และอาจแยกไม่ได้ในธรรมชาติ', 'ทุ่งข้าวโป่งแดง จังหวัดตาก', '54'),
(227, 'นกจับแมลงอกสีฟ้า', 'Hainan Blue Flycatcher', 'Cyornis hainanus', 'ตัวผู้ : หัวและลำตัวด้านบนน้ำเงินแกมฟ้า หน้าผากฟ้าวาวเล็กน้อย คอและอกสีอ่อนกว่า ท้องและสีข้างฟ้าแกมเทาไล่ระดับเป็นสีขาวที่ก้น บางตัวอาจมีแถบคอรูปสามเหลี่ยมสีขาว, ตัวเมีย : หัวและลำตัวด้านบนน้ำตาล คอและอกส้มแกมน้ำตาลคล้ำ ท้องขาว คล้ายกับตัวเมียสกุลเดียวกันชนิดอื่น แต่สีคล้ำกว่า คอและอกแกมสีน้ำตาลแดงอ่อนกว่า ด้านข้างอกสีน้ำตาล', 'เขตรักษาพันธุ์สัตว์ป่าห้วยขาแข้ง จังหวัดอุทัยธานี', '54'),
(228, 'นกจับแมลงพันธุ์จีน', 'Chinese Blue Flycatcher', 'Cyornis glaucicomans', 'ตัวผู้ : คางน้ำเงินเข้มแคบ ๆ หรืออาจมองไม่เห็น คอมเว้าสีส้มแกมน้ำตาลแดงคล้ายนกจับแมลงคอน้ำตาลแดงแต่แหลมกว่า อกสีส้มแกมน้ำตาลแดงคล้ำกว่าต่อกับสีข้างน้ำตาล ลำตัวด้านบนน้ำเงินเข้มกว่า มีแถบสีฟ้าที่ไหล่และตะโพก, ตัวเมีย : จำแนกจากชนิดอื่นที่คอสีอ่อนกว่า อกสีส้มแกมน้ำตาลแดง สีข้างน้ำตาล', 'อุทยานแห่งชาติศรีสัชนาลัย จังหวัดสุโขทัย', '54'),
(229, 'นกจับแมลงคอน้ำตาลแดง', 'Hill Blue Flycatcher', 'Cyornis banyumas', 'ตัวผู้ : หัวและลำตัวด้านบนน้ำเงิน คางและคอเว้าสีส้มแกมน้ำตาลแดงต่อเนื่องกับอกและสีข้าง บางตัวอาจมีสีส้มแกมน้ำตาลแดงเฉพาะคอและอกคล้ายนกจับแมลงอกส้มท้องขาว แต่รอยต่อระหว่างอกกับท้องจะไม่ชัดเจน, ตัวเมีย : คล้ายนกจับแมลงคอสีน้ำเงินเข้ม แต่ข้างหัวเทาแกมน้ำตาลแตกต่างจากคอสีส้มชัดเจนมากกว่า หัวตาสีเข้มกว่า', 'อุทยานแห่งชาติภูสวนทราย จังหวัดเลย', '54'),
(230, 'นกจับแมลงดำอกสีส้ม', 'Mugimaki Flycatcher', 'Ficedula mugimaki', 'ตัวผู้ : หัวและลำตัวด้านบนดำ หลังตามีแถบคล้ายคิ้วสั้น ๆ สีขาว ปีกมีแถบขาวขนาดใหญ่ คอ อก และท้องด้านบนสีส้มเข้ม ท้องด้านล่างและก้นขาว หางดำ ขอบขนหางคู่นอกด้านบนขาว, ตัวเมีย : หัวและลำตัวด้านบนน้ำตาล ปีกและหางดำคล้ำ มีแถบขาวที่ปีกเล็กกว่าตัวผู้ คอและอกน้ำตาลเหลืองแกมส้ม, ตัวผู้ไม่เต็มวัย : คล้ายตัวเมีย แต่ปีกมีแถบน้ำตาลเหลือง', 'สวนหลวง ร.9 และ อำเภอตะพานหิน จังหวัดพิจิตร', '54'),
(231, 'นกจับแมลงหลังเขียว', 'Green-backed Flycatcher', 'Ficedula elisae', 'ตัวผู้ : หัวและลำตัวด้านบนเขียวไพลแกมเทา หัวตาและหนังรอบตาเหลือง ลำตัวด้านล่างและตะโพกเหลือง ปีกมีแถบขาว หางดำ, ตัวเมีย : หลังสีอ่อนกว่าตัวผู้ คล้ายตัวเมียนกจับแมลงคิ้วเหลืองมาก แต่วงรอบตาเหลืองจาง ๆ ลำตัวด้านล่างเหลืองอ่อนแกมขาว หางแกมน้ำตาลแดง', 'ทุ่งข้าวโป่งแดง จังหวัดตาก', '54'),
(232, 'นกจับแมลงคิ้วเหลือง', 'Narcissus Flycatcher', 'Ficedula narcissina', 'ตัวผู้ : คล้ายนกจับแมลงตะโพกเหลืองมาก แต่คิ้วยาวสีเหลือง ท้องและก้นสีขาวมากกว่า แถบปีกขาวเล็กกว่า, ตัวเมีย : หัวและลำตัวด้านบนน้ำตาลมากกว่านกจับแมลงหลังเขียว ลำตัวด้านล่างขาว มีลายจุดเล็ก ๆ ที่คอและอก อกและสีข้างเทาแกมน้ำตาล หางแกมน้ำตาลแดง', 'อำเภอตะพานหิน จังหวัดพิจิตร', '54'),
(233, 'นกจับแมลงตะโพกเหลือง', 'Yellow-rumped Flycatcher', 'Ficedula zanthopygia', 'ตัวผู้ : หัวและลำตัวด้านบนดำ คิ้วและแถบปีกขาว ลำตัวด้านล่างและตะโพกเหลืองสด ในฤดูร้อนอกแกมสีส้ม, ตัวเมีย : ลำตัวด้านบนน้ำตาล ปีกดำแกมน้ำตาลมีแถบขาว ตะโพกเหลือง, ตัวผู้วัยอ่อน : คล้ายตัวเมีย แต่ตะโพกบริเวณติดกับโคนหางด้านบนดำ แถบปีกขาว', 'เขตรักษาพันธุ์สัตว์ป่าห้วยขาแข้ง จังหวัดอุทัยธานี', '54'),
(234, 'นกจับแมลงอกส้มท้องขาว', 'Indochinese Blue Flycatcher', 'Cyornis tickelliae', 'ตัวผู้ : คล้ายนกจับแมลงคอน้ำตาลแเดงมาก จำแนกจากคอและอกส้มเหลืองซึ่งตัดกับท้องสีขาวชัดเจน, ตัวเมีย : จำแนกจากตัวเมียสกุลเดียวกันที่หัว ลำตัวด้านบน และหางเทาแกมฟ้า คอและอกส้มตัดกับท้องคล้ายตัวผู้', 'วัดพระพุทธบาทเขารวก จังหวัดพิจิตร', '54'),
(235, 'นกเขียวก้านตองหน้าผากสีทอง', 'Golden-fronted Leafbird', 'Chloropsis aurifrons', 'ขนลำตัวเขียว หน้าผากเหลืองส้มสด หน้าและคอดำ ใต้คางฟ้าแกมม่วง นกวัยอ่อน : คางและคอเขียว หน้าผากแซมเหลืองจาง ๆ', 'อุทยานแห่งชาติน้ำหนาว จังหวัดเพชรบูรณ์', '55'),
(236, 'นกกาฝากปากหนา', 'Thick-billed Flowerpecker', 'Dicaeum agile', 'ปากหนา ตาสีน้ำตาลแดง ลำตัวด้านบนน้ำตาล แถบหนวดและลำตัวด้านล่างขาว เส้นจากมุมปาก ขีดบนอกและสีข้างน้ำตาลจาง ๆ แต่เห็นได้ชัดเจน ปลายขนหางคู่นอกขาว ขณะเกาะมักส่ายหางไปมา', 'อุทยานแห่งชาติคลองวังเจ้า จังหวัดกำแพงเพชร', '56'),
(237, 'นกสีชมพูสวน', 'Scarlet-backed Flowerpecker', 'Dicaeum cruentatum', 'ตัวผู้ : ข้างหัว อกตอนบน หลัง และหางดำ หน้าผากถึงตะโพกเป็นแถบยาวสีแดงสด คอ กลางอก และลำตัวด้านล่างขาว สีข้างเทาแกมดำ, ตัวเมีย : หัวและลำตัวด้านบนน้ำตาล ปีกและหางดำ ตะโพกและขนคลุมหางแดงสด ลำตัวด้านล่างขาวแกมเทา', 'เขตรักษาพันธุ์สัตว์ป่าห้วยขาแข้ง จังหวัดอุทัยธานี', '56'),
(238, 'นกกินปลีคอสีน้ำตาล', 'Brown-throated Sunbird', 'Anthreptes malacensis', 'ตัวผู้ : หัวถึงหลังตอนบนเขียวเข้มเหลือบเป็นมัน ข้างแก้มและคอน้ำตาล กลางคอจางหรือแกมขาวกว่าส่วนอื่น แถบหนวดและไหล่ม่วงเหลือบเป็นมัน โคนปีกน้ำตาลแดง ตะโพกน้ำเงินแกมม่วงเหลือบเป็นมัน หางเขียวคล้ำ ลำตัวด้านล่างเหลืองเข้ม, ตัวเมีย : ขนาดใหญ่กว่านกกินปลีเปลือกเหลือง ลำตัวด้านบนเหลืองแกมเขียว ลำตัวด้านล่างเหลืองเข้ม เหนือตาและใต้ตามีเส้นเหลืองอ่อนคล้ายวงตาแต่ไม่ต่อเนื่องกัน', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '57'),
(239, 'นกกินปลีดำม่วง', 'Purple Sunbird', 'Cinnyris asiaticus', 'ตัวผู้ : หัว อก และลำตัวด้านบนม่วงแกมน้ำเงินเข้มเหลือบเป็นมัน อกตอนล่างมีแถบน้ำตาลแดงเข้ม ลำตัวด้านล่างสีเข้มกว่าหลัง ปีก และหางดำ ในที่แสงน้อยมักเห็นเป็นนกสีดำ, ตัวผู้ผลัดขน : คล้ายนกกินปลีอกเหลืองตัวผู้ช่วงผลัดขนมาก แต่ปีกสีเข้มกว่า มักมีสีน้ำเงินเข้มเป็นมันที่หัวปีก, ตัวเมีย : คล้ายนกกินปลีอกเหลืองตัวเมียมาก แต่สีอ่อนกว่า ลำตัวด้านล่างขาวมากกว่า แต้มขาวที่ปลายหางแคบกว่า', 'อำเภอขาณุวรลักษบุรี จังหวัดกำแพงเพชร', '57'),
(240, 'นกกินปลีอกเหลือง', 'Olive-backed Sunbird', 'Cinnyris jugularis', 'ตัวผู้ : ลำตัวด้านบนเขียวแกมน้ำตาล หน้าผาก คอถึงอกน้ำเงินเข้มเหลือบเป็นมัน ตอนล่างมีแถบน้ำตาลแดง ลำตัวด้านล่างเหลืองสด ขอบหางคู่นอกและใต้หางขาว, ตัวผู้ผลัดขน : คิ้วเหลือง มีสีน้ำเงินเข้มเฉพาะกลางคอถึงกลางอก, ตัวเมีย : ลำตัวด้านบนเขียวคล้ำแกมเหลือง คิ้วเหลือง ลำตัวด้านล่างเหลืองหม่นกว่าตัวผู้ ขอบหางคู่นอกและใต้หางขาว', 'อำเภอด่านซ้าย จังหวัดเลย', '57'),
(241, 'นกกินปลีแก้มสีทับทิม', 'Ruby-cheeked Sunbird', 'Chalcoparia singalensis', 'ตัวผู้ : หัวและลำตัวด้านบนเขียวเหลือบเป็นมัน แก้มน้ำตาลแดง คอและอกตอนบนส้ม อกตอนล่างและท้องเหลืองสด, ตัวเมีย : ลำตัวด้านบนน้ำตาลแกมเขียว จำแนกจากนกกินปลีอื่น ๆ ที่คอและอกสีส้มแต่สีอ่อนกว่าตัวผู้เล็กน้อย', 'วัดพระพุทธบาทเขารวก อำเภอตะพานหิน จังหวัดพิจิตร', '57'),
(242, 'นกกระจอกใหญ่', 'House Sparrow', 'Passer domesticus', 'ตัวผู้ : หน้าผากและกระหม่อมเทา หลังตาถึงท้ายทอยน้ำตาลแดง แก้มขาว คอและอกดำ ขนาดและความเข้มแตกต่างกันไป หลังน้ำตาลแดงมีลายขีดดำและเทา ตะโพกเทาแกมน้ำตาล ลำตัวด้านล่างเทาแกมขาว, ตัวเมีย : ปากสีเนื้อ หัวและลำตัวน้ำตาลแกมเทา มีคิ้วสีจางกว่า ปีกและหลังน้ำตาลแกมเหลืองอ่อน คล้ายตัวเมียของนกกระจอกตาล แต่หลังมีลายขีดสีน้ำตาลจาง ๆ', 'อำเภอวชิรบารมี จังหวัดพิจิตร', '58'),
(243, 'นกกระจอกบ้าน', 'Eurasian Tree Sparrow', 'Passer montanus', 'ปากดำ หัวน้ำตาลแดงเข้ม แก้มขาวมีแถบดำ คอดำ ลำตัวด้านบนน้ำตาลแดงมีลายดำ ปีกน้ำตาลแดงมีแถบแคบ ๆ สีขาวพาด ลำตัวด้านล่างน้ำตาลแกมเทา นกวัยอ่อน : ลำตัวสีคล้ำ โคนปากสีอ่อน แถบดำที่แก้มไม่มีหรือเห็นไม่ชัดเจน', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '58'),
(244, 'นกกระจอกตาล', 'Plain-backed Sparrow', 'Passer flaveolus', 'ตัวผู้ : หลังน้ำตาลแดงไม่มีลาย หลังตาถึงข้างหัวน้ำตาลแดง กระหม่อมและหลังตอนบนเทา คอดำ แก้มและลำตัวด้านล่างเหลืองอ่อน ตัวเมีย : คล้ายนกกระจอกใหญ่ตัวเมีย แต่หลังไม่มีลาย ลำตัวด้านล่างเหลืองอ่อนแกมน้ำตาล', 'อำเภอวชิรบารมี จังหวัดพิจิตร', '58'),
(245, 'นกกระจาบทอง', 'Asian Golden Weaver', 'Ploceus hypoxanthus', 'ตัวผู้ขนชุดผสมพันธุ์ : หัว ตะโพก และลำตัวด้านล่างเหลืองสด ตัดกับแถบหน้าและคอดำ ลำตัวด้านบนดำแกมน้ำตาลมีลายเหลือง หางดำ, ตัวผู้นอกฤดูผสมพันธุ์และตัวเมีย : คล้ายนกกระจาบธรรมดามาก แต่ปากหนาอวบและสั้นกว่า', 'บึงกะโล่ จังหวัดอุตรดิตถ์', '59'),
(246, 'นกกระติ๊ดแดง', 'Red Avadavat', 'Amandava amandava', 'ตัวผู้ขนชุดผสมพันธุ์ : ปากและขนลำตัวแดงสด แถบตาดำแคบ หลังแกมน้ำตาล ปีกและหางดำ มีจุดขาวกระจายที่ลำตัวด้านล่าง ปีก และตะโพก, ตัวผู้นอกฤดูผสมพันธุ์ : คล้ายตัวเมีย แต่จุดขาวใหญ่กว่า ตะโพกมีจุดขาว, ตัวเมีย : แถบตาดำ ปากและตะโพกแดง ลำตัวด้านบนและล่างน้ำตาล ปีกสีเข้มกว่ามีจุดขาวเล็ก ๆ', 'คลองหนองหลวง จังหวัดพิษณุโลก', '60'),
(247, 'นกกระติ๊ดตะโพกขาว', 'White-rumped Munia', 'Lonchura striata', 'ปากเทา ตะโพกและท้องขาว ตัดกับหัว อก และลำตัวด้านบนน้ำตาลเข้ม หน้าและปีกน้ำตาลดำ มีลายขีดสีอ่อนที่หลังและอก ปลายขนหางยื่นเป็นติ่งแหลมชัดเจนกว่าชนิดอื่น', 'อำเภอกงไกรลาศ จังหวัดสุโขทัย', '60'),
(248, 'นกกระติ๊ดขี้หมู', 'Scaly-breasted Munia', 'Lonchura punctulata', 'ปากดำ หัว อก และลำตัวด้านบนน้ำตาลเข้ม ลำตัวด้านล่างขาวแกมเทามีลายเกล็ดสีน้ำตาล กลางท้อง ก้นขาว หางน้ำตาลแกมเหลือง นกวัยอ่อน : ลำตัวสีน้ำตาลอ่อนกว่า ไม่มีลายเกล็ดที่อก ปากล่างสีอ่อนกว่าปากบน', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '60'),
(249, 'นกกระติ๊ดสีอิฐ', 'Chestnut Munia', 'Lonchura atricapilla', 'ปากเทาแกมฟ้า หัวและอกดำ ตัดกับขนลำตัวสีน้ำตาลแดง กลางท้องและก้นน้ำตาลเข้มเกือบดำ นกวัยอ่อน : ลำตัวสีน้ำตาลเรียบ ๆ คล้ายนกกระติ๊ดหัวขาววัยอ่อน แต่กระหม่อมสีเข้มกว่า ปากเทาแกมฟ้า', 'สำนักงานประมง จังหวัดนครสวรรค์', '60'),
(250, 'นกเด้าลมดง', 'Forest Wagtail', 'Dendronanthus indicus', 'คิ้วขาว แถบหน้าสีคล้ำ ขนลำตัวน้ำตาลแกมเขียว ลำตัวด้านล่างขาวแกมเหลืองมีแถบหนาสีดำพาดอก  2 แถบ แต่แถบล่างไม่ต่อกัน ปีกดำมีแถบขาวหรือแกมเหลืองอ่อน', 'อุทยานแห่งชาติรามคำแหง จังหวัดสุโขทัย', '61'),
(251, 'นกเด้าลมเหลือง', 'Eastern Yellow Wagtail', 'Motacilla tschutschensis', 'คล้ายนกเด้าลมหลังเทา แต่ขณะบินไม่มีแถบขาวที่ปีก ตะโพกสีเดียวกับหลัง หางสั้นกว่าเล็กน้อย ขนลำตัวด้านบนน้ำตาลแกมเขียว ขนลำตัวด้านล่างมีตั้งแต่ขาวแกมเหลืองถึงเหลืองสด ตัวผู้ขนชุดผสมพันธุ์ : ชนิดย่อย thunbergi หัวถึงท้ายทอยเทามีคิ้วยาวแคบ ๆ สีขาว ลำตัวด้านบนเขียวแกมเหลืองคล้ำ อกและลำตัวด้านล่างเหลือง ชนิดย่อย macronyx ไม่มีคิ้วขาว หัวและท้ายทอยเทาเข้ม คางขาว คอและลำตัวด้านล่างเหลืองเข้ม ชนิดย่อย taivana แตกต่างจากชนิดย่อยอื่นที่คิ้วยาวสีเหลืองชัดเจน หัวและลำตัวด้านบนเขียวคล้ำ, ตัวเมีย : ลำตัวด้านล่างเหลืองจาง ๆ หรือขาว อาจมีคิ้วยาวสีขาว', 'ทุ่งข้าวศรีสัชนาลัย จังหวัดสุโขทัย', '61'),
(252, 'นกเด้าลมหัวเหลือง', 'Citrine Wagtail', 'Motacilla citreola', 'คล้ายนกเด้าลมเหลืองตัวเมีย แต่หัวตาและคิ้วเหลืองเด่นชัด หน้าคล้ำ อาจมีแถบหนวดสีเข้ม คอและอกแกมเหลือง กระหม่อม ท้ายทอยและหลังเทา ปีกดำมีแถบปีกขาว อกและลำตัวด้านล่างขาวแกมเทา ก้นขาว ตัวผู้ขนชุดผสมพันธุ์ : หางและลำตัวด้านล่างเหลืองสดใส ก้นขาว หลังตอนบนและข้างอกดำ แถบปีกขาวชัดเจน', 'บึงกะโล่ จังหวัดอุตรดิตถ์', '61'),
(253, 'นกเด้าลมหลังเทา', 'Grey Wagtail', 'Motacilla cinerea', 'คิ้วยาวแคบ ๆ สีขาว หัวและลำตัวด้านบนเทา ปีดำมีลายจากขอบขนโคนปีกขาว คอขาว ท้องแกมเหลืองและเข้มขึ้นที่ก้น ตะโพกเหลืองเห็นได้ชัดเจนขณะบิน หางเรียวยาวสีดำขอบหางคู่นอกขาว ขณะบินปีกมีแถบขาว ตัวผู้ขนชุดผสมพันธุ์ : คิ้วและแถบหนวดขาวชัดเจน คอและอกตอนบนดำ ลำตัวด้านล่างเหลืองสดขึ้น', 'อุทยานแห่งชาติศรีสัชนาลัย จังหวัดสุโขทัย', '61'),
(254, 'นกเด้าดินทุ่งใหญ่', 'Richard\'s Pipit', 'Anthus richardi', 'ชนิดย่อย richardi ขนาดใหญ่กว่านกเด้าดินชนิดอื่น คิ้วสีน้ำตาลอ่อนต่อเนื่องกับแถบข้างคอ แถบตาสีคล้ำ ลำตัวด้านบนสีน้ำตาลแกมเหลืองมีลายขีดสีคล้ำบนหัวและหลัง ลำตัวด้านล่างน้ำตาลอ่อน แถบหนวดดำต่อเนื่องกับลายจุดและลายขีดที่อกและสีข้าง คล้ายนกเด้าดินทุ่งพันธุ์รัสเซียมาก แต่ปากยาวกว่า แข้งแและหางยาวกว่าเล็กน้อย ลายบนลำตัวจางกว่าเล็กน้อย ลักษณะปลายสีดำของขนคลุมปีกชั้นกลางแหลมกว่า เล็บนิ้วตีนหลังยาวกว่าแต่โค้งน้อยกว่า ชนิดย่อย sinesis ขนาดเล็กกว่า คล้ายนกเด้าดินทุ่งเล็ก', 'ทุ่งข้าวศรีสัชนาลัย จังหวัดสุโขทัย', '61'),
(255, 'นกเด้าดินทุ่งเล็ก', 'Paddyfield Pipit', 'Anthus rufulus', 'คล้ายนกเด้าดินทุ่งใหญ่ แต่ขนาดเล็กและเพรียวกว่าชัดเจนเมื่อยืนเปรียบเทียบกัน ปากและหางสั้นกว่าเล็กน้อย โคนปากล่างสีเหลืองมากกว่านกเด้าดินทุ่งใหญ่และนกเด้าดินพันธุ์รัสเซีย คอมักเป็นสีน้ำตาลอ่อนใกล้เคียงกับอก ลำตัวด้านล่างมีลายหนาแน่นที่อก', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '61'),
(256, 'นกอุ้มบาตร', 'White Wagtail', 'Motacilla alba', 'ขนลำตัวสลับดำเด่นชัด หลังสีดำหรือเทา ลักษณะแตกต่างกันไปตามชนิดย่อย และอาจจำแนกเป็นชนิดที่แตกต่างกันดังนี้ 1. Amur Wagtail : M. leucopsis ตัวผู้ : ลำตัวด้านล่างขาว กระหม่อม ท้ายทอย ลำตัวด้านบน และแถบอกดำ ขนคลุมปีกด้านบนและขอบขนโคนปีกขาว ขอบหางขาว ฤดูผสมพันธุ์แถบดำที่อกใหญ่ขั้น ขอบต่อเนื่องกับสีดำข้างอก, ตัวเมีย : คล้ายตัวผู้แต่ลำตัวด้านบนเทา 2. Himalayan Wagtail : M. alboides ตัวผู้ : หน้าผาก คิ้ว คางและคอตอนบนขาว ขนคลุมหู คอ และแถบอกดำ ฤดูผสมพันธุ์คางและอกดำ, ตัวเมีย :  คล้ายตัวผู้แต่ลำตัวด้านบนแกมสีเทา 3. Baikal Wagtail : M.baicalensis ตัวผู้ : หลังและลำตัวด้านบนเทา แถบที่อกใหญ่กว่า leucopsis, ตัวเมีย : กระหม่อมสีหม่นกว่าตัวผู้ ลำตัวด้านบนสีเทาอ่อนกว่า leucopsis 4. East Siberian Wagtail : M.Ocularis หลังและลำตัวด้านบนเทาคล้าย bailcalensis แต่แถบตาดำ ฤดูผสมพันธุ์คางและอกดำ แถบตาดำชัดเจนขึ้น ตัวเมีย : กระหม่อมและท้ายทอยเทา', 'มหาวิทยาลัยนเรศวร จังหวัดพิษณุโลก', '61'),
(257, 'นกเด้าดินสวน', 'Olive-backed Pipit', 'Anthus hodgsoni', 'หัวและลำตัวด้านบนเขียวมะกอก หลังมีลายขีดยาวสีคล้ำจาง  ๆ หัวตาน้ำตาลแกมเหลืองต่อเนื่องกับคิ้วยาวสีขาว แถบหน้าเขียวมะกอก ขอบด้านท้ายเป็นเส้นดำ มีจุดขาวเล็ก ๆ ใต้ปลายคิ้ว แถบหนวดดำ ต่อเนื่องกับจุดใหญ่และหนาสีดำกระจายถึงท้องและสีข้าง คอ อกและท้องขาวแกมน้ำตาลเหลือง', 'ศูนย์บริการนักท่องเที่ยวอุทยานแห่งชาติแม่วงก์ จังหวัดกำแพงเพชร', '61'),
(258, 'นกจาบปีกอ่อนอกเหลือง', 'Yellow-breasted Bunting', 'Emberiza aureola', 'ตัวผู้ : หัวน้ำตาลแกมแดง คิ้วและข้างคอเหลือง แถบข้างกระหม่อมสีคล้ำ แก้มและขนคลุมหูน้ำตาลแดงขอบดำ ลำตัวด้านล่างเหลือง มีแถบสีน้ำตาลแดงไม่ต่อเนื่องคาดอก มีลายขีดจาง ๆ ที่สีข้าง ลำตัวด้านบนน้ำตาลแกมแดง ปีกมีแถบขาว, ตัวผู้ขนชุดผสมพันธุ์ : หน้าดำ ลำตัวด้านล่างเหลืองสดใสมีแถบน้ำตาลแดงคาดอก ลำตัวด้านบนน้ำตาลแดงเข้ม, ตัวเมีย : คล้ายตัวผู้นอกฤดูผสมพันธุ์ ไม่มีแถบขาวที่ปีก ลำตัวด้านล่างเหลืองอ่อน ลำตัวด้านบนน้ำตาลมีลายขีดดำ', 'บึงกะโล่ จังหวัดอุตรดิตถ์', '62'),
(259, 'นกจาบปีกอ่อนหัวดำ', 'Black-headed Bunting', 'Emberiza melanocephala', 'เป็นนกจาบปีกอ่อนขนาดใหญ่ ตัวผู้ : หัวและหน้าดำ ลำตัวด้านล่างสีเหลืองอ่อน ลำตัวด้านบนสีน้ำตาลแดง ในขนชุดฤดูผสมพันธุ์ หัวและหน้าดำ ตัดกับลำตัว ด้านล่างสีเหลืองสดใส, ตัวเมีย : ต่างจากตัวเมียของนกจาบปีกอ่อนอื่น ๆ ที่หัวเทาแกมน้ำตาล ไม่มีคิ้วหรือแถบหนวด ลำตัวด้านบนสีน้ำตาลอ่อน มีลายสีเข้มกว่า อกและลำตัวด้านล่างเหลืองอ่อน คอสีจาง แถบปีกสีน้ำตาลอ่อน ตะโพกสีแซมน้ำตาลอ่อน', 'บึงกะโล่ จังหวัดอุตรดิตถ์', '62'),
(260, 'นกจาบปีกอ่อนสีกุหลาบ', 'Common Rosefinch', 'Erythrina erythrina', 'ตัวผู้ : ปากอวบสั้น หัวและอกชมพูแดงสดใส ลำตัวด้านบนและปีกเทาแกมดำ มีลายขีด และแถบปีกสีชมพูคล้ำ ท้องด้านล่างและก้นขาว, ตัวเมีย : ลำตัวด้านบนสีน้ำตาล ลำตัวด้านล่างสีอ่อนกว่า มีลายขีดดำที่อกและหลัง', 'อุทยานแห่งชาติภูชี้ฟ้า จังหวัดเชียงราย', '63'),
(525, 'dd', 'dd', 'dd', 'dd', 'dd', '63');
